// Here are the translations.
// The last array ends without comma.
var langs = new Array(
	new Array( '.bg', 'Арена', 'Ниво', 'Гали', 'Римляни', 'Тевтонци', 'Разстояние', 'Задай като начална точка' ), // Български
	new Array( '.com', 'Tournament Square', 'Level', 'Gauls', 'Romans', 'Teutons', 'Distance', 'Set as start point' ) // English
);



function getVars(){
	winloc = window.location + ' ';
	xStart = GM_getValue("xVal");
	yStart = GM_getValue("yVal");
	zArena = GM_getValue("aLevel");
}



function getCoords(coordlet){
	maincontent = document.getElementById('content');
	h1title = maincontent.getElementsByTagName('h1');
	h1tstr = h1title[0].innerHTML;
	if ( coordlet == 'x' ) xyVals = h1tstr.substring( h1tstr.lastIndexOf( "(" ) + 1,h1tstr.lastIndexOf( "|" ) );
	else xyVals = h1tstr.substring( h1tstr.lastIndexOf( "|" ) + 1,h1tstr.lastIndexOf( ")" ) );
	xyVals *= 1;
	return xyVals;
}



function setXYValues(){
	GM_setValue("xVal",getCoords('x'));
	GM_setValue("yVal",getCoords('y'));
	distanceblank();
	getTime();
}



function addZero(num){
	if ( num < 10 ) num = '0' + num;
	return num;
}



function calcDistance(){
	getVars();
	if (winloc.indexOf('&c=') == -1){
		xValue = document.getElementById('x').innerHTML;xValue *= 1;
		yValue = document.getElementById('y').innerHTML;yValue *= 1;
	}
	else {
		xValue = getCoords('x');
		yValue = getCoords('y');
	}
	cDistance = Math.sqrt( Math.pow( ( ( Math.max( xStart, xValue ) ) - ( Math.min( xStart, xValue ) ) ), 2 ) +
	Math.pow( ( ( Math.max( yStart, yValue ) ) - ( Math.min( yStart, yValue ) ) ), 2 ) );
	cDistance = Math.round(1000*cDistance)/1000;
	return cDistance;
}



function arenaBonus(){
	aValue = document.getElementById('arena').value;
	GM_setValue("aLevel",aValue);
	getTime();
	document.getElementById('arena').addEventListener("change", arenaBonus, true);
	document.getElementById('setValLink').addEventListener("click", setXYValues, true);
}



function calcTime(troop,distance,ttype) {

	if (winloc.indexOf('speed') != -1){
		troop *= 3;
	}

	aBon = distance - 30;
	aBon = Math.round(1000 * aBon) / 1000;

	if ( ( aBon <= 0 ) || (ttype == 10) ) {
		hours = Math.floor( distance / troop );
		distance = distance - hours * troop;

		troop = troop / 60;
		minutes = Math.floor( distance / troop );
		distance = distance - minutes * troop;

		troop = troop / 60;
		seconds = Math.floor( distance / troop );
	}
	else {
		troop2 = troop * (1 + 0.1 * zArena);

		distance = 30;
		hours = Math.floor( distance / troop );
		distance = distance - hours * troop;

		troop = troop / 60;
		minutes = Math.floor( distance / troop );
		distance = distance - minutes * troop;

		troop = troop / 60;
		seconds = distance / troop;

		hours2 = Math.floor( aBon / troop2 );
		aBon = aBon - hours2 * troop2;

		troop2 = troop2 / 60;
		minutes2 = Math.floor( aBon / troop2 );
		aBon = aBon - minutes2 * troop2;

		troop2 = troop2 / 60;
		seconds2 = aBon / troop2;

		hours = hours + hours2;
		minutes = minutes + minutes2;
		seconds = Math.round( seconds + seconds2 );

		if ( seconds >= 60 ) {
			minutes++;
			seconds -= 60;
		}
		if ( minutes >= 60 ) {
			hours++;
			minutes -= 60;
		}
	}
	cTime = hours + ':' + addZero(minutes) + ':' + addZero(seconds);
	return cTime;
}



function buildSelect(){
	zArena = GM_getValue("aLevel");
	if (winloc.indexOf('&c=') == -1){
		sBox = zArena;
	}
	else {
		sBox = '<select id="arena">';
		for (q = 0; q < 21; q++) {
			if ( q == zArena )
			sBox += '<option value="' + q + '" selected="selected">' + q + '</option>';
			else
			sBox += '<option value="' + q + '">' + q + '</option>';
		}
		sBox += '</select>'
	}
	return sBox;
}



function addLink(){
	var linktable = document.getElementById("options");
	newrow = linktable.insertRow(-1);
	newcell = newrow.insertCell(0);
	newcell.innerHTML =  '<a id="setValLink" href="#">&raquo; ' + langs[lang][7] + '</a>';
}



function getTime(){
	resstr = '<table style="background:#000000;" cellspacing="1" border="0">';
	resstr += '<tr><td colspan="3" style="padding:3px;background:#ffffff;">' + langs[lang][1] + ': ' + langs[lang][2] + ' ' + buildSelect()+ '</td></tr>';
	resstr += '<tr><td style="background:#ffffff;text-align:center;">' + langs[lang][3] + '</td><td style="text-align:center;">' + langs[lang][4] + '</td><td style="text-align:center;">' + langs[lang][5] + '</td></tr>';
	for (x = 0; x < troops[0].length; x++) {
		resstr += '<tr>';
		for (y = 0; y < troops.length; y++) {
		resstr += '<td style="padding:3px;background:#ffffff;"><img src="'+troops[y][x] + '" style="vertical-align: bottom;" /> ' + calcTime( tspeed[y][x], calcDistance(), x) + '</td>';
		}
		resstr += '</tr>';
	}
	resstr += '<tr><td colspan="3" style="padding:3px;background:#ffffff;"> (' + xStart + '|' + yStart + ') ' + langs[lang][6] + ':' + calcDistance() + '</td></tr>';
	document.getElementById('timestats').innerHTML = place + resstr + '</table>';
}



function distanceblank(){
	document.getElementById('timestats').innerHTML = place;
}



function setMouseOver(){
	for (i = 0; i < 7; i++) {
		for (j = 0; j < 7; j++) {
			item = 'a_' + i + '_' + j;
			document.getElementById(item).addEventListener("mouseover", getTime, true);
			document.getElementById(item).addEventListener("mouseout", distanceblank, true);
		}
	}
}



function newArray(x,y){
	a = new Array(x);
	for(i = 0; i <= x; i++){
		a[i] = new Array(y);
		for(j = 0; j <= y; j++){
		a[i][j] = '';
		}
	}
	return(a);
}



