// ==UserScript==
// @name          Last.fm Torrent Search Links
// @updateURL     http://userscripts.org/scripts/source/20252.meta.js
// @version		  2012.12.09
// @description   Script to add some search links to last.fm artist, album, similar-artists and recommended-artists pages.
// @include       http://last.fm/music/*
// @include       http://lastfm.*/music/*
// @include       http://www.last.fm/music/*
// @include       http://www.lastfm.*/music/*
// @include       http://last.fm/home/recs*
// @include       http://lastfm.*/home/recs*
// @include       http://www.last.fm/home/recs*
// @include       http://www.lastfm.*/home/recs*
// @include       http://playground.last.fm/sterec/*
// @include       http://playground.last.fm/multitag/artists/*
// @include       http://rateyourmusic.com/artist/*
// @include       http://rateyourmusic.com/release/*
// @include       http://rateyourmusic.com/collection/*
// @include       http://www.discogs.com/*/release/*
// @include       http://www.discogs.com/*/master/*
// @include       http://www.discogs.com/artist/*
// @include       /^https?:\/\/(?:\w+\.)?what\.cd?\/torrents\.php.*/
// @include       /^https?:\/\/(?:\w+\.)?what\.cd?\/artist\.php.*/
// @include       /^https?:\/\/(?:\w+\.)?jpopsuki\.eu?\/artist\.php.*/
// @require       https://raw.github.com/sizzlemctwizzle/GM_config/master/gm_config.js
// @require       http://ajax.googleapis.com/ajax/libs/jquery/1.8.2/jquery.min.js
// ==/UserScript==
// 2012-12-09 update: Added searches to (from) jpopsuki artist pages; fixed a couple of Discogs problems.

////////////////
// Main logic //
////////////////

function main() {
	var site = $.grep(sites, function(site){return location.href.match(site.url)})[0];
	if(!site) return;
	if(site.pre) site.pre();
	$(site.mainPath).each(function(i, node){makeLinks(site, node);});
	$(searches).each(function(i,search){
		var links = $("span.torrentLinkSpan>a."+search.title.replace('.',"\\."));
		search.hide = function(){links.hide();}
		search.show = function(){links.show();}
		if(search.post) links.each(function(i, link){search.post(link);});
		if(GM_getValue(search.title, false)) search.hide();
	});
	populateMenu();
}
// Add entries to the GreaseMonkey menu
function populateMenu()	{
	for(var i = 0; i < searches.length; ++i) 
		GM_registerMenuCommand(
			"Toggle " + searches[i].title + " link", 
			function(search) {
				return function() {
					if(GM_getValue(search.title, false)) {
						GM_deleteValue(search.title); 
						search.show();
					}
					else { 
						GM_setValue(search.title, true);
						search.hide();
					}
				}
			}(searches[i])
		);	
}
// Make the links for node (i.e. a particular artist or album)
function makeLinks(site, node) {
	var container = $("<span class='torrentLinkSpan'></span>");
	var artistName = getText(site.artistPath ? $(site.artistPath, node) : $(node));
	var albumName = site.albumPath ? getText($(site.albumPath, node)) : "";
	if(albumName && site.albumFunction) albumName = site.albumFunction(albumName).replace(/^\s+/,"");
	for(j = 0;search=searches[j];j++)
		if(search != site.search) addLink(container, search, {artist:artistName, album:albumName});
	if (site.beforePath) $(site.beforePath, node).before(container);
	else (site.placePath ? $(site.placePath, node) : $(node)).append(container);		
}
// Make a link and put it in container
function addLink(container, search, parameters) {
	var url = (parameters.album?(search.albumTemplate):(search.artistTemplate)) || search.template;
	var a = $("<a style='margin-left:5px' class='"+search.title + "'></a>");
	var img = $("<img title='"+search.title+"'>")
	a.attr("href", makeUrl(url, parameters));
	img.attr("src", search.imgsrc);
	a.append(img);
	container.append(a);
}
// Replace "%foo%" in url with parameter[foo].
function makeUrl(url, parameters) {
	for(p in parameters) url = url.replace('%'+p+'%', parameters[p])
	return url;
}
// Convert Cyrillic characters to the format used by RuTracker.
function rtpost(link) {
	link.href = link.href.replace(/%D0%9/g,"%C");
	link.href = link.href.replace(/%D0%A/g,"%D");
	link.href = link.href.replace(/%D0%B/g,"%E");
	link.href = link.href.replace(/%D1%8/g,"%F");
}
// Get the text in the node in question, removing html escaping and adding URL escaping
function getText(jqObject){
	return encodeURIComponent(
		unescape(
			jqObject.text().replace(/\s\(\d+\)$/,"").replace(/^[\s(]+/,"").replace(/[\s)]+$/,"")
		)
	);
}

