// This is easy:
function $(id) { return document.getElementById(id); }
function $g(m) {
	var mask = new RegExp(m);
	var r = mask.test(window.location.pathname);
//	alert('[mask]: ' + m + ' [res]: ' + res + ' [path]: ' + window.location.pathname);
	return r;
};
