// Applying style
function $a(w) {
	if (typeof GM_addStyle != 'undefined') {GM_addStyle(w)}
	else if (typeof addStyle != 'undefined') {addStyle(w)}
	else {
		var heads = document.getElementsByTagName('head');
		if (heads.length > 0)
			{
			var node = document.createElement('style');
				node.type = 'text/css';
				node.innerHTML = w;
			heads[0].appendChild(node); 
			};
		};
// --
};
