// ==UserScript==
// @name           PS3fanboy.com
// @namespace      ps3fanboy
// @description    This script changes the visuals of PS3fanboy.com.
// @include        *.ps3fanboy.com/*
// @author         sibbor;at;gmail;dot;com
// @version        0.1.4
// @date           2008-02-27
// ==/UserScript==

// Background images:
var bg_body = 'data:image/jpg;base64,';
var bg_content = 'data:image/jpg;base64,';
var bg_byline = 'data:image/jpg;base64,';

// CSS:
var css = "@namespace url(http://www.w3.org/1999/xhtml);"
		// Change basic appearance:
		+ "* { font-family: 'Trebuchet MS', Tahoma, 'Trebuchet MS', sans-serif; }"
		+ "body { background: #000 url("+ bg_body +") top center repeat-y; color: #FFF; margin: -15px 20px 20px 20px; }"
		+ "input { background-color: #FFF; color: #666; }"
		+ "#container { width: 896px; background: none }"
		+ "#content { width: 846px; background: #424242 url("+ bg_content +") top center repeat-x; padding: 20px; border: 5px solid #333; position: relative; left: -14px; top: -10px; }"
		+ "#content div { background: #222; margin: 10px 0; padding: 10px 10px 0 10px; -moz-border-radius: 5px; }"


		// Hide elements & objects (mostly adds)
		+ "iframe, .topleader, .bottomleader, #outerslice, #hat, #branding, #footer, #grid, .medrect, .pinboxes, #linkscol, #statscol { display: none; }"
		+ "#bp1, #bp2, #bp3, #bp4, #bp5 { display: none; }"
		+ "#bp6, #bp7, #bp8, #bp9, #bp10 { display: none; }"


		// Headers
		+ "* h1, * h2, * h3, * h4, * h5, * h1 *, * h2 *, * h3 *, * h4 *, * h5 * { font-family: 'Century Gothic', Tahoma, 'Trebuchet MS', san-serif; padding: 0; margin: 0; font-style: normal; font-weight: normal; color: #09B; }"
		+ "h1 { font-size: 1em; }"
		+ "h2 { font-size: 1.2em; }"
		+ "h3 { font-size: 1.4em; }"
		+ "legend { font-family: 'Century Gothic', Tahoma, sans-serif; text-transform: uppercase; }"


		// Links:
		+ "#content a { color: #0099FF; padding: 2px; }"
		+ "#content a:hover { background-color: #0099FF; color: #000; -moz-border-radius: 3px; }"
		+ ".byline a, .postmeta a { color: #00FFF9; }"
		+ ".byline a:hover, .postmeta a:hover { background-color: #00FFF9; color: #000; }"
		+ ".continueReading a { color: #9C992E; }"
		+ ".continueReading a:hover { background-color: #9C992E; color: #000; }"
		+ ".postmeta a { color: #FFDD99; }"
		+ ".postmeta a:hover { background-color: #FFDD99; color: #000; }"


		// Search box:
		+ "#subcontent { position: absolute; background: none; width: 891px; height: 0; }"
		+ ".searchform form { float: right; top: -55px; right: 5px; }"
		+ ".searchform { height: 0; }"
		+ ".searchform 	.clearfix { display: none; }"


		// News:
		+ "#content > div > h2 { float: left; }"
		+ "p.byline { color: #FFF; background: #000 url("+ bg_byline +"); }"
		+ ".byline { font-family: 'Century Gothic', Tahoma, sans-serif; line-height: 1.3em; float: left; clear: both; }"
		+ ".byline, .postgallery { padding: 4px; background: #222; border: 1px solid #666; color: #FFF; -moz-border-radius: 4px; }"
		+ ".continueReading { clear: both; }"
		+ ".posttags { padding-top: 5px; }"
		+ ".postgallery { background-color: #111; color: #FFF; }"
		+ ".postgallery p { margin: 0; padding: 0; }"
		+ ".postgallery > p > strong { text-transform: uppercase; font-family: 'Century Gothic', Tahoma, sans-serif; }"
		+ ".post { position: relative; top: 10px; padding: 10px; background-color: #222; color: #FFF; border: 2px #444 solid; -moz-border-radius: 5px; line-height: 1.7em; }"
		+ ".post img { border: 2px solid #333; -moz-border-radius: 2px; }"
		+ ".postmeta > ul { margin-bottom: 30px; padding-bottom: 10px; border: 0; }"
		+ "#content div .postmeta { background: none; }"
		+ "#content div .post, #content div .post div { background: none; }"
		+ "#content div .post { border: 2px solid #333; background-color: #111; }"


		// Polls:
		+ ".post table { background: none; }"
		+ ".post table td { background: #111; font-size: 1em; padding: 0 2px; }"
		+ ".post table font { color: #FFF; font-size: 1em; padding: 5px 5px 0 5px; }"
		+ ".post table strong { font-family: 'Century Gothic', sans-serif; text-transform: uppercase; color: #FFDD99; }"


		// Comments:
		+ "#content #comments div { margin: 0; padding: 0; background: none; }"
		+ "#content #comments { padding-bottom: 10px; }"
		+ "#relatedheadlines, #readercomments { padding: 5px; margin: 0 0 10px 0; }"
		+ "#content #comments h4 { color: #0066CC; font-family: 'Century Gothic'; font-size: 1.1em; }"
		+ "#content #comments h4 a { color: inherit; font-weight:bold; border-bottom: 1px dotted #0066CC; }"
		+ "#content #comments h4 a:hover { background-color: #0066CC; color: #FFF; text-decoration: underline; }"
		
		+ "#comments .rownum { width: 50px; background-color: #000; color: #FFF; font-family: 'Century Gothic'; height: 20px; -moz-border-radius: 4px; }"
		+ "#comments .avatar { border: 2px solid #000; -moz-border-radius: 2px; width: 46px; height: 46px; top: 35px; }"
		+ "#comments .level0 { opacity: 0.3; background-color: rgb(255, 220, 220); border: 1px solid red; }"
		+ "#content #comments p, #content #comments .cmt_votes, #content #comments .authorname { position: relative; top: -10px; }"
		
		+ "#content #comments .cmt_reply { z-index: 1; }"
		+ "#content #comments .cmt_votes { z-index: 0; }"
		
		+ "#content #comments .commentclass1, #content #comments .commentclass2 { margin-top: 2px; padding: 0 10px 15px 70px; border: 2px solid #333; -moz-border-radius: 5px; }"
		+ "#content #comments .commentclass1 { background: #1C1C1C; }"
		+ "#content #comments .commentclass2 { background: #101010; }"
		+ "#content #comments .commentclass1 .cmt_reply a, #content #comments  .commentclass2 .cmt_reply a { text-transform: uppercase; background: #000; border-top: 1px solid #CCC; border-bottom: 1px solid #999; border-right: 1px solid #999; border-left: 1px solid #CCC; -moz-border-radius: 3px; }"
		+ "#content #comments .commentclass1 .cmt_reply a:hover, #content #comments  .commentclass2 .cmt_reply a:hover { background: #111; color: #FFF; border-top: 1px solid #999; border-bottom: 1px solid #CCC; border-right: 1px solid #CCC; border-left: 1px solid #999; }"
		+ "#comments .child { left: 42px; width: 700px; margin-left: 20px; }"
		+ "#commentform { border: 2px solid #333; -moz-border-radius: 5px; background-color: #222; color: #FFF; }"
		+ "#commentform .formerrors { display: none; }"
		
		// "Thank you" message upon commenting:
		+ "div #thankYou h3 { text-transform: uppercase; color: #09B; border-bottom: 1px dotted #FFF; }"
		+ "div #thankYou { background-color: rgb(150, 200, 200); border: 2px solid #000; padding: 10px; }"


		// Gallery:
		+ "#main > #content { margin-top: 40px;  background-color: #444; }"
		+ "#header { background-color: #222; }"
		+ "#content .breadcrumb a { color: #CCC; font-family: 'Century Gothic'; }"
		+ "#content .breadcrumb em a { text-transform: uppercase; border-bottom: 1px dotted #FFF; }"
		+ "#content .breadcrumb { color: #FFF; background-color: #222; position: relative; top: 5px; -moz-border-radius-topleft: 5px; -moz-border-radius-topright: 5px; }"
		+ ".related-post, .view-hi-res { position: absolute; left: 0; top: 0; z-index: 2; }"
		+ ".thumbstrip { margin: 0; padding: 0; position: absolute; top: -60px; }"

		+ "";

if ( typeof GM_addStyle != 'undefined' ) {
	GM_addStyle(css);
} else if ( typeof addStyle != 'undefined' ) {
	addStyle(css);
} else {
	var heads = document.getElementsByTagName('head');
	if ( heads.length > 0 ) {
		var node = document.createElement('style');
		node.type = 'text/css';
		node.innerHTML = css;
		heads[0].appendChild(node);
	}
}

// Show the searchbox (will cause a JS error):
document.getElementById('branding').style.display = 'block';