// ==UserScript==
// @name           Website Styler
// @namespace      http://userscripts.org/users/23652
// @description    Several customizable options. Special support for Userscripts.org (new interface, fix for pre tags, and allows you to hide the install warning box and footer) Changes text fields, buttons, links, selects, and text areas.
// @include        *
// @exclude       about:*
// @exclude       chrome:*
// @version        2.5
// @copyright      JoeSimmons
// ==/UserScript==
var hideNoticeError,hideFooter,hideFeedbackTab,link_size,link_size_hover,link_padding,
link_padding,select_border_color,button_textfield_padding,button_textfield_padding_hover,
link_border_color_hover,button_textfield_border_color,button_textfield_border_color_hover,
round_fields_intensity,link_color,link_color_hover,textarea_border_color,textarea_border_color_hover,
modify_links,modify_buttons,modify_textareas,modify_selects,i;



//////////////////////////////////////////////////////////////////////////
//////////////////////////////////////////////////////////////////////////
// OPTIONS ///////////////////////////////////////////////////////////////
hideNoticeError = true; // Hide the caution box when installing scripts //
hideFooter = true;      // Hide the footer ///////////////////////////////
hideFeedbackTab = true; // Hide the orange feedback tab on the left //////
////////////////////////// Userscripts.org only //////////////////////////

modify_links = false;
modify_buttons = true;
modify_textareas = true;
modify_selects = true;

link_size = "11";
link_size_hover = "11";

link_padding = ".1";
link_padding_hover = ".1";
button_textfield_padding = "2px 8px";
button_textfield_padding_hover = "2px 8px";

// Colors in hex or word (eg., #FF0000 or white)
// http://www.colorschemer.com/online.html
link_color = "#0078F0";
link_color_hover = "#bbb";
select_border_color = "#bbb";
link_border_color_hover = "#000";
button_textfield_border_color = "#A4A4A4";
button_textfield_border_color_hover = "#3781B0";
textarea_border_color = "#bbb";
textarea_border_color_hover = "#5F5F5F";

// This option is for how rounded the fields are
round_fields_intensity = "3";
//////////////////////////////////////////////////////////////////////////
//////////////////////////////////////////////////////////////////////////
//////////////////////////////////////////////////////////////////////////

// Remove element by id
function rID(e, pnode) {
var _e = document.getElementById(e);
if(pnode===true) {_e = _e.parentNode;}
if(_e) {
_e.parentNode.removeChild(_e);
return true;
}
else {return false;}
}

// Remove ID by Website
function removeID(ID, Site, Pnode) {
var id, site, ss, re, www, sSearch, pNode=Pnode||false;
if(!document.getElementById(ID)) {return false;}
re=/^http(s?)\:\/\//;
sSearch=/^https/;
site=(!re.test(Site))?'http://'+Site:Site;
if(Site.indexOf('www.')==-1) {
www=site.replace(re, "www.");
www=(sSearch.test(site))?'https://'+www:'http://'+www;
}
if(location.href.indexOf(site)!==-1 || location.href.indexOf(www)!==-1) {rID(ID,Pnode);}
}

// XPath but the array returned is a normal array[x]
// Syntax: $x("//a", 7);
function $x(p, type) {
  var i, arr = [], t = type || 6, xpr = document.evaluate(p,document,null,t,null);
  for (i = 0; item = xpr.snapshotItem(i); i++) {arr.push(item);}
  return arr;
}

// addGlobalStyle
function addGlobalStyle(css) {
    var head, style;
    head = document.getElementsByTagName('head')[0];
    if (!head) { return; }
    style = document.createElement('style');
    style.type = 'text/css';
    style.innerHTML = css;
    head.appendChild(style);
}

// Remove element by id
function removeElemById(e) {
var _e = document.getElementById(e);
if(_e) {
_e.parentNode.removeChild(_e);
return true;
}
else {return false;}
}

// Remove nodes through xpath by JoeSimmons
function removeXP(xp) {
var x,XP=document.evaluate(xp,document,null,6,null);
for(var i=0; i<XP.snapshotLength; i++) {var x=XP.snapshotItem(i);x.parentNode.removeChild(x);}
}

function insertAfter(newElement,targetElement) {
//target is what you want it to go after. Look for this elements parent.
var parent = targetElement.parentNode;
//if the parents lastchild is the targetElement...
if(parent.lastchild == targetElement) {
//add the newElement after the target element.
parent.appendChild(newElement);
} else {
// else the target has siblings, insert the new element between the target and it's next sibling.
parent.insertBefore(newElement, targetElement.nextSibling);
}
}

function addcss() {
css = "";
if(modify_buttons) {css += ""
+"input, button {\n"
+"color:#000;\n"
+"background:#DCDCDC url(data:image/png;base64,) repeat-x;\n"
+"-moz-border-radius:"+round_fields_intensity+"px;\n"
+"margin:1px;\n"
+"border:1px solid "+button_textfield_border_color+";\n"
+"padding:"+button_textfield_padding+";\n"
+"font:9pt arial verdana \"sans-serif\";\n"
+"}\n"
+"input:hover, button:hover {\n"
+"background:#A8DBF8 url(data:image/png;base64,) repeat-x;\n"
+"border:1px solid "+button_textfield_border_color_hover+";\n"
+"}\n"
;
}
if(modify_textareas) {css += 
"textarea {\n"
+"color:#000;\n"
+"border:1px solid "+textarea_border_color+";\n"
+"font:9pt arial,verdana,\"sans-serif\";\n"
+"-moz-border-radius:"+round_fields_intensity+"px;\n"
+"background:url(data:image/png;base64,);\n"
+"}\n"
+"textarea:hover {\n"
+"border:1px solid "+textarea_border_color_hover+";\n"
+"}\n";}
if(modify_selects) {css += 
"select {\n"
+"color:#000;\n"
+"background:url(data:image/png;base64,);\n"
+"border:1px solid "+select_border_color+";\n"
+"font-family:arial,verdana,\"sans-serif\";\n"
+"}\n";}
if(modify_links) {css += 
"a {\n"
+"color:"+link_color+";\n"
+"font:"+link_size+" arial,verdana,\"sans-serif\";\n"
+"border:1px solid transparent;\n"
+"padding:"+link_padding+"em;\n"
+"}\n"
+"a:hover {\n"
+"color:"+link_color_hover+";\n"
+"font-size:"+link_size_hover+"px;\n"
+"border:1px solid "+link_border_color_hover+";\n"
+"padding:"+link_padding_hover+"em;\n"
+"-moz-border-radius:"+round_fields_intensity+"px;\n"
+"}\n";}

css = css.replace(/;$/gm, ' !important;');
typeof GM_addStyle != "undefined" ? GM_addStyle(css) : addGlobalStyle(css);
css=null;delete css;
}

function uso() {
document.images[0].src = "data:image/png;base64,";
if(hideNoticeError) {removeElemById("install_warning");}
if(hideFooter) {removeElemById("footer");}
if(hideFeedbackTab) {removeElemById("uservoice-feedback-tab");}
removeID('monitor_label', 'userscripts.org/forums', true); // Hide the 'watch topic' box
removeXP("/html/body/div[2]/div[3]");
removeXP("/html/body/div[2]/h2");
removeXP("//hr");
var css = "#header \n{\nbackground:url(data:image/png;base64,);\nborder-bottom:1px solid #000;\npadding:0em;\n}\n"
+          ".notice,.error\n{\nbackground:transparent;\ncolor:#000;\nborder:1px solid #B5B5B5;\n}\n"
+          "#mainmenu li a\n{\ncolor:#08f;\n}";
if(location.href.indexOf("http://userscripts.org/forums/")==0) {
css+="#content {width:"+(window.innerWidth/1.5)+"px;}";
removeID('right', 'userscripts.org/forums');
}
css=css.replace(/;$/gm, ' !important;');
typeof GM_addStyle != "undefined" ? GM_addStyle(css) : addGlobalStyle(css);
css=null;delete css;
}

addcss();
if(location.href.indexOf("http://userscripts.org/")==0) window.addEventListener("load",uso,false);