	function addStat(name, value, extension)
	{
		if(extension==null || extension==''){
			extension = 'deviant-stats';
		}
		if (value != null && value!=''){
			document.getElementById(extension).innerHTML+=  '<strong>' + value + ' <\/strong>' + name + ' <br>';
		} else {
			document.getElementById(extension).innerHTML+= name + ' <br>';
		}
	}
	
	function addExtensionsStat(name, value, extension)
	{
		addStat(name, value, extension);
	}
	
	
	
	
