/*
 Ok, lets face it... Scroogle is a good idea but it could use some work. 1995
 called and they want there layout back, and don't get me started with
 google-watch.org. It's bad on the eyes and almost impossible to work with.
 This script should make things more bearable.

Version 1.2 2011-09-10
 - Increased the separation between results to 10px

Version 1.2 2010-03-08
 - Will now focus on search box

Version 1.1 2010-03-08
 - Previously entered text now appears in the text box

Version 1.0 2010-03-04
 - Initial Release

To-do list
 - Compatibility testing; I only use Firefox so if anyone could help me out on this...
*/

// addGlobalStyle borrowed from http://diveintogreasemonkey.org/ - Thanks Mark
function addGlobalStyle(css) {
	var head, style;
	head = document.getElementsByTagName('head')[0];
	if (!head) { return; }
	style = document.createElement('style');
	style.type = 'text/css';
	style.innerHTML = css;
	head.appendChild(style);
}

// Why do they insist on using #cccc99 as the background color? WHY?
addGlobalStyle('body { background-color: white; }');

if (/nbbwssl/.test(window.location.href)) {
	// CSS
	addGlobalStyle('body { font-size:0px ! important; }'); // Hide some stuff, like numbering
	addGlobalStyle('a { font-size: medium; }'); // We care about this
	addGlobalStyle('b { background-color: white ! important; }'); // Remove highlighting, but keep it bold
	addGlobalStyle('ul { color: black; list-style-type: none; padding: 0; margin: 0; margin-bottom: 10; }'); // Remove list indents

	// Capture query value
	var form = document.getElementsByTagName('form')[0];
	var input = form.elements.namedItem("Gw");
	var query = input.value;

	// Remove the search boxes at the top and bottom of the results page
	var rmFormTop = document.getElementsByTagName('center')[0];
	if (rmFormTop) {rmFormTop.parentNode.removeChild(rmFormTop);}
	var rmFormBot = document.getElementsByTagName('center')[0];
	if (rmFormBot) {rmFormBot.parentNode.removeChild(rmFormBot);}

	var altForm = document.createElement("center");
	altForm.innerHTML = '<font style="color: black; font-family:Arial,Helvetica,sans-serif;" size=2">'+
	'<center><form method="post" action="https://ssl.scroogle.org/cgi-bin/nbbwssl.cgi">'+
	'<br><input type="text" name="Gw" size="40" maxlength="225" value="'+query+'">'+
	'&nbsp;&nbsp;<input type="submit" value="Search">'+
	'<br>Number of results: &nbsp;'+
	'<input type=radio name="n" value="2">20 &nbsp; &nbsp;'+
	'<input type=radio name="n" value="5">50 &nbsp; &nbsp;'+
	'<input type=radio name="n" value="1" checked>100'+
	'&nbsp; &nbsp; <input type="checkbox" name="z" value="1">Next 100'+
	'&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</form><br></font></center>';
	document.body.insertBefore(altForm,document.body.firstChild);
} else {
	// Oh f*** me... Just remove everything and start over
	var rmBody = document.getElementsByTagName('body')[0];
	if (rmBody) {rmBody.parentNode.removeChild(rmBody);}

	// Body
	var headTag = document.getElementsByTagName('head')[0];
	var altBody = document.createElement("body");
	altBody.innerHTML = '<font style="color: black; font-family:Arial,Helvetica,sans-serif;" size=2">'+
	'<center><br><br><br><form method="post" action="https://ssl.scroogle.org/cgi-bin/nbbwssl.cgi">'+
	'<br><input type="text" name="Gw" id="Gw" size="40" maxlength="225">'+
	'&nbsp;&nbsp;<input type="submit" value="Search">'+
	'<br>Number of results: &nbsp;'+
	'<input type=radio name="n" value="2">20 &nbsp; &nbsp;'+
	'<input type=radio name="n" value="5">50 &nbsp; &nbsp;'+
	'<input type=radio name="n" value="1" checked>100'+
	'&nbsp; &nbsp; <input type="checkbox" name="z" value="1">Next 100'+
	'&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</form></font></center>';
	headTag.parentNode.insertBefore(altBody,headTag.nextSibling);
	document.getElementById("Gw").focus();
}

