function result_sort(field) {
	var ordername = "";
	if ($ljq('#sort_'+field).hasClass("asc")) {
		ordername = "-"+field;
		ord = "desc";
	} else {
		ordername = field;
		ord = "asc";
	}
	var result = jLinq.from(list.chart)
		//orderby name = asc, -name = desc
		//.orderBy(o+"year")
		.orderBy(ordername)
    	.select();
	//alert(JSON.stringify(result));
	tmpresult = result;
	showsorted();
}

