function showsorted() {
	var result = tmpresult;
	tmpresult = "";
	var rows = "<table border=\"1\" cellspacing=\"0\" cellpadding=\"4\">";
	rows += "<tr bgcolor=\"#FFFFDB\">"+rowheader+"</tr>";
	//var numr = "";
	//alert(list.chart[1].rank);
	for (i=0; i<result.length; i++) {
		if (i % 2) {
			rows += '<tr bgcolor="#ffffff" valign="top"><td align="right"><font face="Arial, Helvetica, sans-serif" size="-1"><b>'+result[i].rank+'.</b></font></td><td align="center"><font face="Arial, Helvetica, sans-serif" size="-1">'+result[i].rating+'</font></td><td><font face="Arial, Helvetica, sans-serif" size="-1"><a href="'+result[i].url+'">'+result[i].title+'</font></td><td align="right"><font face="Arial, Helvetica, sans-serif" size="-1">'+result[i].votes+'</font></td></tr>';
		} else {
			rows += '<tr bgcolor="#e5e5e5" valign="top"><td align="right"><font face="Arial, Helvetica, sans-serif" size="-1"><b>'+result[i].rank+'.</b></font></td><td align="center"><font face="Arial, Helvetica, sans-serif" size="-1">'+result[i].rating+'</font></td><td><font face="Arial, Helvetica, sans-serif" size="-1"><a href="'+result[i].url+'">'+result[i].title+'</font></td><td align="right"><font face="Arial, Helvetica, sans-serif" size="-1">'+result[i].votes+'</font></td></tr>';
		}
	}
	rows += "</table>";
	
	//rows = "";
	//for (i=0; i<result.length-1; i++) {
	//	rows += result[i].rank+" "+result[i].title+"\n";
	//}
	
	//alert(rows);
	$ljq("#imdbsort").html(rows);
	//sortarrow($ljq(this).text());
	//sortarrow(header);
	
	header = header.toLowerCase();
	//alert(header);
	//var order = $ljq("#sort_"+header).attr("class");
	//alert(ord);
	$ljq("#sort_rank,#sort_rating,#sort_title,#sort_year,#sort_votes").attr("class", "");
	if (ord == "desc") {
		$ljq("#sort_"+header).attr("class", "desc");
	} else {
		$ljq("#sort_"+header).attr("class", "asc");
	}
	header = "";
	ord = "";
}

