// ==UserScript==
// @namespace     http://www.squarefree.com/jsenv/autogenerated
// @name          SU IMDB 
// @description   Undescribed script
// @include       http://*.stumbleupon.com/about*
// ==/UserScript==

var span = document.getElementsByTagName('span');
var ans = "";
for (var i = span.length - 1; i > 0; i--) {
	if (span[i].innerHTML.indexOf("Movies") > -1 && span[i].getAttribute("class") == "text1") {
		ans = span[i];
		break;
	}
}
if (ans != "") {	
	ans = ans.parentNode.parentNode.parentNode.parentNode.parentNode.parentNode;
	ans = ans.nextSibling;
	lnk = ans.getElementsByTagName('a');
	for (var j = lnk.length - 1; j >= 0; j--) {
		var add = lnk[j].parentNode;
		var box = document.createElement('a');
		box.href="http://www.imdb.com/find?q=" + lnk[j].innerHTML;
		box = add.insertBefore(box, lnk[j]);
		box.innerHTML = "";
		box.style.textDecoration = "none";
		box.appendChild(document.createElement('img')).src = "data:image/png;base64,";
		box.appendChild(document.createTextNode(' '));
	}
}

