// ==UserScript==
// @name           Nzb browsers links for Tugabin
// @namespace      www.forum.tugabin.com
// @description    Add nzb browsers links on Tugabin forum.
// @include        http://www.forum.tugabin.com/*
// @author Guile93
// ==/UserScript==
function make_link(elem,cible,title,img){
	var newLink = document.createElement("a");
	newLink.href=cible;
	newLink.target="_blank";
	var new_img = document.createElement("img");
	new_img.src=img;
	new_img.title="Pesquisa no "+title;
	new_img.alt="Pesquisa no "+title;
	newLink.appendChild(new_img);
	elem.appendChild(newLink);
}
function nzb_link(elem,cible,title,img){
	var newLink = document.createElement("a");
	newLink.href=cible;
	newLink.target="_self";
	var new_img = document.createElement("img");
	new_img.src=img;
	new_img.title="Download NZB pelo "+title;
	new_img.alt="Download NZB pelo "+title;
	newLink.appendChild(new_img);
	elem.appendChild(newLink);
}
function download_link(elem,cible,title,img){
	var newLink = document.createElement("a");
	newLink.href=cible;
	newLink.target="_self";
	var new_img = document.createElement("img");
	new_img.src=img;
	new_img.title="Download com "+title;
	new_img.alt="Download com "+title;
	newLink.appendChild(new_img);
	elem.appendChild(newLink);
}
function add_links(){
	var search_array = document.getElementsByTagName("code");
	for (var n=0; n < search_array.length; ++n){
				var code = search_array[n];
				var newelem=code.previousSibling;
		if(newelem.innerHTML.match(/Seleccione/)){
				var req=code.innerHTML.replace(/ /g, "+");
				req=req.split("*").join(" "); 
				var req_amp=req.replace("&amp;","&");
				var method1=escape(req_amp);
				var method2=encodeURI(req_amp);
				var method3=encodeURI(req).replace("&amp;","%26");
				var div = document.createElement("div");
				make_link(div,"http://www.nzbclub.com/nzbsearch.aspx?ss="+method1+"&rpp=200&rs=1&sa=1","NZBClub","data:image/x-png;base64,");
				make_link(div,"http://www.binsearch.info/?max=250&adv_age=&server=1&q="+method1,"Binsearch","data:image/x-png;base64,");
				make_link(div,"http://www.binsearch.info/?max=250&adv_age=&server=2&q="+method1,"Binsearch \"other groups\"","data:image/x-png;base64,");
				make_link(div,"http://www.newzleech.com/?mode=usenet&adv=&q="+method1,"Newzleech","data:image/x-png;base64,");
				make_link(div,"http://www.yabsearch.nl/search/"+method2,"Yabsearch","data:image/png;base64,");
				make_link(div,"http://www.nzbindex.nl/index.php?go=search&age=&results=250&sort=age_desc&searchitem="+method3,"NZBindex.nl","data:image/x-png;base64,");
				make_link(div,"http://search.aeton.biz/?q="+method1+"&adv_g=&adv_age=&max=1000","Aeton","data:image/x-png;base64,");
				div.style.display="none";
				newelem.addEventListener( "mouseover", make_visible, false );
				newelem.appendChild(div);
		}
	}
}
function add_nzb_links(){
	var moteurs_array=new Array();
	moteurs_array[0]="NZBClub";
	moteurs_array[1]="http://www.nzbclub.com/nzb_download.aspx?mid=";
	moteurs_array[2]="NZBIndex";
	moteurs_array[3]="http://nzbindex.nl/download/";
	moteurs_array[4]="Yabse";
	moteurs_array[5]="http://www.yabsearch.nl/nzb/";
	moteurs_array[6]="Newzleech";
	moteurs_array[7]="http://www.newzleech.com/?m=gen&dl=1&post=";
	for(var m=0; m < moteurs_array.length; m+=2){
		var search_array = document.getElementsByClassName(moteurs_array[m]);
    for (var n=0; n < search_array.length; ++n){
                var code_div = search_array[n];
                var req=code_div.innerHTML.replace(/ /g, "+");
                var div = document.createElement("div");
                nzb_link(div,moteurs_array[m+1]+req,moteurs_array[m],"data:image/x-png;base64,");
                code_div.appendChild(div);
    }
	}
}
function add_gigaflat_link(){
 var search_array = document.getElementsByClassName("Gigaflat");
     for (var n=0; n < search_array.length; ++n){
 var code_div = search_array[n];
 var req=code_div.innerHTML.replace(/ /g, "+");
                 var div = document.createElement("div");
 download_link(div,"gigaflat:"+req,"Gigaflat","data:image/x-icon;base64,");
 code_div.appendChild(div);
    }
}
function make_visible(e){
	var div=e.target.getElementsByTagName("div")[0];
	if(div){
		if(olddiv){
			olddiv.style.display="none";
		}
		olddiv=div;
		div.style.display="block";
	}
}
var olddiv;
add_links();
add_nzb_links();
add_gigaflat_link();