// ==UserScript==
// @name           The West Extra Tools - modified
// @namespace      http://forum.the-west.net/showthread.php?t=22294
// @description    Adds 4 buttons, Westinsider, Weststats, Export to Weststats and WestCalc 
// @include        http://*.the-west.*/game.php*
// @exclude        http://zz1w1.tw.innogames.net/game.php*
// @exclude        http://www.the-west.*
// @exclude        http://forum.the-west.*
// @require        http://userscripts.org/scripts/source/75442.user.js
// @resource  meta http://userscripts.org/scripts/source/74678.meta.js
// ==/UserScript==

// Toggles for the external site buttons
var enable_weststats = true;
var enable_westinsider = true;
var enable_westcalc = false;

/***** WestStats *****/
if ( enable_weststats ) {
	///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
	//                                                                                                                           //
	//    How to enable a button for exporting your inventory and skills to your account at West Stats (disabled by default).    //
	//                                                                                                                           //
	//  To enable the button and make it export to your profile, you need to put your login data into the script.                //
	//                                                                                                                           //
	//  1. Locate [//var weststats_exportID = '&ui=------&up=----------------------------------------'] below this textbox.        //
	//  2. Go to http://www.weststats.com and login.                                                                             //
	//  3. Go to http://www.weststats.com/User_import/ and find the code from step 1. there, in the first line.*                 //
	//  4. Uncomment and edit the weststats_exportID variable below and replace                                                  //
	//      '&ui=------&up=----------------------------------------'  in this script with the code from your weststats.          //
	//     (Yes, it is a bit hard to select just that part of the string because of the automatic selection of the complete      //
	//      code. But that's the only way - because of the use of both " and ' quotes.                                           //
	//  5. Congrats, you have done it. Refresh your browser and see if the new button works!                                     //
	//                                                                                                                           //
	//  *The code will be different than mine, since it contains your personal login data for weststats.                         //
	//                                                                                                                           //
	///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

	//var weststats_exportID = '&ui=------&up=----------------------------------------';
	if ( typeof(weststats_exportID) != "undefined" ) {
		var weststats_script = document.createElement("div");
		weststats_script.id = "weststats_script";
		weststats_script.innerHTML = "<a id=\"TWET_weststats_export\" href=\"javascript:var wsp_auth = '"+ weststats_exportID +"';var wsp_url = 'www.weststats.com';var remoteScript=new Element('script', {'type': 'text/javascript', 'src': 'http://www.weststats.com/js/import_all.js?1247793097'});document.body.appendChild(remoteScript);void(0);\"></a>";
	}

	var actual_world = window.location.host.substr(0,3);
	var actual_region = window.location.host.substr(0,2);

	switch(actual_region){
	case "en":
		actual_region="www";
		break;
	}	

	var weststats_link = document.createElement("div");
	weststats_link.id = "weststats_link";
	// Original link:
	//weststats_link.innerHTML = '<a id="TWET_weststats_link" href="http://'+ actual_region + '.weststats.com/?change_world='+actual_world+'" target="_blank"></a>';
	// Modified link, linking to the job calculator page directly:
	weststats_link.innerHTML = '<a id="TWET_weststats_link" href="http://'+ actual_region + '.weststats.com/Jobs/?type=personal&skill=none&bonus=best&best=equip&itemsetcalc=on&change_world='+actual_world+'" target="_blank"></a>';
}

/***** WestInsider *****/
if ( enable_westinsider ) {
	var westinsider_script = document.createElement("div");
	westinsider_script.id = "westinsider_script";
	westinsider_script.innerHTML = "<a id=\"TWET_westinsider\" href=\"javascript:(function(){%20var%20head%20=%20document.getElementsByTagName('head').item(0);%20var%20old%20=%20document.getElementById('TWIjs');%20if(old)%20head.removeChild(old);%20var%20js%20=%20document.createElement('script');%20js.setAttribute('id',%20'TWIjs');%20js.setAttribute('language',%20'javascript');%20js.setAttribute('type',%20'text/javascript');%20js.setAttribute('src',%20'http://www.TheWestInsider.com/scripts/1.js.php');%20head.appendChild(js);%20}%20)()\"></a>";
}

/***** WestCalc *****/
if ( enable_westcalc ) {
	var westcalc_script = document.createElement("div");
	westcalc_script.id="westcalc_script";
	westcalc_script.innerHTML = "<a id=\"TWET_westcalc\" href=\"javascript:var%20rs=document.createElement('script');%20rs.setAttribute('type','text/javascript');%20rs.setAttribute('src','http://www.westcalc.info/wcjs/wcF.js?123');%20rs.setAttribute('id','myremote');%20if(document.getElementById('myremote')!=null){%20document.body.removeChild(document.getElementById('myremote'))%20}document.body.appendChild(rs);%20void%20(0);\"></a>";
}
/***** place the new buttons *****/
var menu_work = document.getElementById('menu_work');
if (menu_work) {
	if ( enable_weststats ) {
		menu_work.parentNode.insertBefore(weststats_link, menu_work.nextSibling);
		if ( typeof(weststats_exportID) != "undefined" ) {
			menu_work.parentNode.insertBefore(weststats_script, menu_work.nextSibling);
		}
	}
};
var menu_inventory = document.getElementById('menu_inventory');
if (menu_inventory) {
	if ( enable_westcalc ) {
		menu_inventory.parentNode.insertBefore(westcalc_script, menu_inventory.nextSibling);
	}
	if ( enable_westinsider ) {
		menu_inventory.parentNode.insertBefore(westinsider_script, menu_inventory.nextSibling);
	}
};

// Change left and right workbar heights based on the amount of buttons added to either side
var workbarLeftTop  = Number(getComputedStyle(document.getElementById('workbar_left'), '').top.replace('px',''));
var workbarRightTop = Number(getComputedStyle(document.getElementById('workbar_right'), '').top.replace('px',''));
workbarLeftTop += ((enable_westinsider + enable_westcalc) * 25);
workbarRightTop += ((enable_weststats + (typeof(weststats_exportID) != "undefined")  ) * 25);

/***** Data-URI embedded images *****/
var cssel = document.createElement("style");
cssel.setAttribute("type", "text/css");
cssel.textContent = 
"#workbar_left { top: "+ workbarLeftTop + "px !important; }"+
"#workbar_right { top: "+ workbarRightTop + "px !important; }"+
"#weststats_link {"+
"background:url(data:image/png;base64,)"+
"} "+
"#weststats_script {"+
"background:url(data:image/png;base64,)"+
"} "+
"#westinsider_script {"+
"background:url(data:image/png;base64,)"+
"} "+
"#westcalc_script {"+
"background:url(data:image/png;base64,)"+
"}";
document.getElementsByTagName('head')[0].appendChild(cssel);