// ==UserScript==
// @name           IMDb full titles in photo galleries
// @namespace      http://userscripts.org/users/67626
// @description    In the title filter in photo galleries, adds the year & type of the titles.
// @include        http://*.imdb.com/name/*/mediaindex
// @include        http://*.imdb.com/name/*/mediaindex?ref_=*
// @include        http://*.imdb.com/name/*/mediaindex?page=*
// @include        http://*.imdb.com/character/*/mediaindex
// @include        http://*.imdb.com/character/*/mediaindex?ref_=*
// @include        http://*.imdb.com/character/*/mediaindex?page=*
// @grant          GM_xmlhttpRequest
// @updateURL      http://userscripts.org/scripts/source/50101.meta.js
// @version        2014.01.18
// ==/UserScript==

(function() {
if (window.top != window.self) return; //iframes

function $(x) {return document.getElementById(x)}
var RHS_Titles = $("media_index_title_filters");
if (RHS_Titles) {
	function getFullTitle(T) {
		GM_xmlhttpRequest({
			method: "GET",
			url: "http://www.imdb.com/title/" + T.search.split("=")[1].split("&")[0] + "/taglines",
			onload: function(response) {
				T.innerHTML = T.innerHTML.split(" <img", 1)[0];
				var title = response.responseText.split("</title>", 1)[0].split("<title>")[1];
				if (title) {
					var Span = document.createElement("span");
					Span.innerHTML = T.innerHTML.link(T.href) + "<small>" + title.match(/ \(.+\)/) + "</small>";
					T.parentNode.replaceChild(Span, T);
				}
			}
		});
	}
	var Full = document.createElement("span");
	Full.innerHTML = '<button class="btn secondary" style="position: absolute; margin: -2px auto auto 25px; padding: 1px 5px" title="Retrieve full titles" id="Full">Full titles</button>';
	RHS_Titles.getElementsByTagName("h4")[0].appendChild(Full);
	$("Full").addEventListener("click", function() {
		Full.parentNode.removeChild(Full);
		var A = RHS_Titles.getElementsByTagName("a");
		for (var i = 0; i < A.length; i++)
			if (A[i].search.match("refine=tt")) {
				A[i].innerHTML += ' <img title="Loading..." src="data:image/gif;base64," />';
				getFullTitle(A[i]);
			}
	}, false);
}

})();