	function getLanguageAndPlusStatus() {
		if (TB3O.T35 == false) {
			//for Travian older versions
			iP = find("//img[contains(@src, 'plus.gif')]", XPFirst);
			if (iP) {
				iP.src.search(/\/img\/([^\/]+)\//);
				TB3O.language = RegExp.$1.substring(0,2);
			}
			if (find("//img[contains(@src, 'travian1.gif')]", XPFirst)) TB3O.plusAcc = true;
			iP = null;
		} else {
			//for Travian 3.5
			iP = get("logo");
			ahref = null;
			if (iP) {
				if (iP.nodeName == "IMG") {
					TB3O.M35 = 1;
					if (iP.className && (iP.className == "plus" || iP.className == "logo_plus")) TB3O.plusAcc = true;
					ahref = iP.parentNode.href;
				} else if (iP.nodeName == "A") {
					if (iP.firstChild && iP.firstChild.className == "logo_plus") TB3O.plusAcc = true;
					ahref = iP.href;
					TB3O.M35 = 2;
				}
				if (ahref) {
					aLang = ahref.split(".");
					TB3O.language = aLang[aLang.length - 1].replace("/", "");
				}
				iP = null;
				ahref = null;
			}
		}
	}

