	function getColorResBarTooltip(p) {
		//provided by Acr111 (adapted by ms99)
		trigPr = 90;
		if (p < trigPr) prC = "rgb(" + parseInt(p / trigPr * 255) + "," + (100 + p) + ",0)"; else prC = "rgb(255," + parseInt((100 - p) / (100 - trigPr) * 170) + ",0)";
		return prC;
	}

	function updateResbarTooltip() {
		getFillTimeRow();
		for (var i = 0; i < 4; i++) {
			procNo = Math.round(crtResUnits[i] / capacity[i] * 100);
			if (procNo > 100) procNo = 100;
			prC = getColorResBarTooltip(procNo);
			aSpan = get("resbarProc_" + i);
			if (aSpan != null) {
				prSpan = updProcResBarTooltip(i, procNo, prC);
				aSpan.parentNode.replaceChild(prSpan, aSpan);
			}
			rbT = get("resbarTable_" + i);
			if (rbT != null) {
				bTable = updColTableResBarTooltip(i, procNo, prC);
				rbT.parentNode.replaceChild(bTable, rbT);
			}
		}
	}

	function createFloatingDiv(dWidth, posX, posY, strTitle, sCookieN, divID, boolShowMinMax) {
		wClose = 25;
		wCMM = 25;
		intHeight = 20;
		bckgColor = "#ECECEC";
		iPx = parseInt(posX);
		if (iPx < 5) iPx = 0;
		iPy = parseInt(posY);
		if (iPy < 5) iPy = 0;
		if (boolShowMinMax == true) wCMM = 2 * wClose;

		div = newDiv("", [['id', divID], ["style", "position:absolute; width:" + dWidth + "px; top:" + iPy + "px; left:" + iPx + "px; display:block; padding:1px; z-index:50; clear:both; border:2px solid #C0C0C0; background-color:#FFFFFF;"]]);
		if (strTitle == T('VILLAGELIST')) strTitle = "<a href='dorf3.php'>" + strTitle + "</a>";
		dragDiv = newDiv(strTitle, [['id', 'dragDiv_' + sCookieN], ["style", "text-align:center; font-weight:bold; height:" + intHeight + "px; width:" + (dWidth - wCMM) + "px; float:" + docDir[0] + "; cursor: pointer; border-bottom:1px solid #C0C0C0; background-color:" + bckgColor + ";"]]);

		if (boolShowMinMax == true) {
			newState = getGMcookie(sCookieN + "_state", false);
			if (newState == "false") newState = "max";
			mmDiv = newDiv("", [["style", "height:" + intHeight + "px; width:" + wClose + "px; float:" + docDir[0] + "; border-bottom:1px solid #C0C0C0; background-color:" + bckgColor + ";"]]);
			strImgMM = "minButton";
			if (newState == "min") strImgMM = "maxButton";
			mmImage = newImage([['src', image[strImgMM]], ['style', 'cursor:pointer']]);
			if (divID && divID != "") mmImage.addEventListener("click", function() {
				if (newState == "min") newState = "max"; else newState = "min";
				if (sCookieN && sCookieN != "") setGMcookie(sCookieN + "_state", newState, false);
				removeElement(get(divID));
				switch (sCookieN) {
					case "showresbartable": showResBarTooltip(); break;
					case "showbookmarks": showUserBookmarks(); break;
					case "noteblock": showNoteBlock(); break;
					case "showvl2table": show2ndVillageList(); break;
					case "showsearchbar": showSearchBar(); break;
				}
			}, false);
			mmDiv.appendChild(mmImage);
		}

		closeDiv = newDiv("", [["style", "height:" + intHeight + "px; width:" + wClose + "px; float:" + docDir[0] + "; border-bottom:1px solid #C0C0C0; background-color:" + bckgColor + ";"]]);
		xImg = newImage([['src', image["closeButton"]], ['style', 'cursor:pointer'], ['title', T('CLOSE')]]);
		if (divID && divID != "") xImg.addEventListener("click", function() {get(divID).style.display = "none"; if (sCookieN && sCookieN != "") setGMcookie(sCookieN, "0", false);}, false);
		closeDiv.appendChild(xImg);

		makeDraggable(div, dragDiv);

		div.appendChild(dragDiv);
		if (boolShowMinMax && mmDiv) div.appendChild(mmDiv);
		div.appendChild(closeDiv);
		document.body.appendChild(div);
		return div;
	}

