		//append a row for total crop consumption
		bRow = newRow("", [['class', 'tb3r']]);
		fCell = newCell(gIcons["r5"], [['class', 'tb3c'], ['colspan', '2']]);
		e1Cell = newCell("", [['class', 'tb3c']]);
