		rCP1.appendChild(c11);
		var c12 = newCell(aV[1], [['class', 'tb3chb'], ['colspan', 2], ['style', 'font-size:8pt;']]);
		rCP1.appendChild(c12);
		var c13 = newCell(gIcons["clock"], [['class', 'tb3chb'], ['colspan', 2]]);
		rCP1.appendChild(c13);
