		function addUpdateTroopsPower(i, troopLink) {
			return function() {
				aNo = parseInt(troopLink.textContent.replace("(", "").replace(")", ""));
				troopInput = get("t" + i);
				if (troopInput) {
					troopInput.value = aNo;
					updateTroopsPower();
				}
			}
		}

		function updateTroopsPower() {
			totals = [["troopsattpower", "att_all", 5, 0], ["troopsdefipower", "def_i", 6, 0], ["troopsdefcpower", "def_c", 7, 0], ["troopscapacity", "capacity", 4, 0], ["troopscropconsumption", "img5", 9, 0]];
			for (var i = 1; i < 11; i++) {
				tInput = get("t" + i);
				if (tInput) {
					if (TB3O.T35 == false) {
						tImg = tInput.parentNode.previousSibling.firstChild;
					} else {
						tImg = tInput.parentNode.childNodes[1];
					}
					tType = getTroopIndexTitleFromImage(tImg)[0];
					if (tInput.value != "") {
						intTinput = parseInt(tInput.value);
						for (var j = 0; j < 5; j++) {
							totals[j][3] += intTinput * uc[tType][totals[j][2]];
						}
					}
				}
			}
			for (var j = 0; j < 5; j++) {
				aCell = get(totals[j][0]);
				if (aCell) {
					strX = " *";
					switch (j) {
						case 0:
						case 1:
						case 2: imgPath = gIcons[totals[j][1]]; break;
						case 3: imgPath = gIcons["capacity"]; strX = ""; break;
						case 4: imgPath = gIcons["r5"]; strX = ""; break;
					}
					aCell.innerHTML = imgPath + strX + " " + getLS(totals[j][3]);
				}
			}
			return;
		}

		function clearAllTroops() {
			for (var i = 1; i < 12; i++) {
				troopInput = get("t" + i);
				if (troopInput) troopInput.value = '';
			}
			updateTroopsPower();
			return;
		}

