			strSearch = "//form[@name='snd']//table//input[(@type='text' or @type='Text') and not (@name='x') and not (@name='y')]";
			if (TB3O.T35 == false) strSearch = "//table[@class='p1']//table[@class='f10']//input[@type='Text' or @type='text']";
