						iHTML += "<td class='tb3cnb' ><img " + imgName + "></td><td class='tb3cnb' style='font-size:8pt; text-align:" + properAlign +";'>" + "&nbsp;" + formatTime(aTime, 0) + " h</td><td>&nbsp;&nbsp;&nbsp</td>";

						var imgNo = iTT + arZ[3];
