	//update the script (by Richard Gibson; changed by ms99)
	function updateScript() {
		var divUpdate = newDiv("<b>" + T('CHECKUPDATE') + "</b>");
		var a = get(dmid1);
		if (a) a.appendChild(divUpdate);
		try {
			if (!GM_getValue) {return};
			GM_xmlhttpRequest({
				method: 'GET',
				url: TB3O.url + '?source', // don't increase the 'installed' count; just for checking
				onload: function(result) {
					removeElement(divUpdate);
					if (result.status != 200) {return;}
					if (!result.responseText.match(/@version\s+([\d.]+)/)) {return;}
					var theNewVersion = RegExp.$1;
					if (theNewVersion == TB3O.version) {
						alert(T('NONEWVERSION') + ' (v ' + TB3O.version + ') !');
						return;
					} else if (theNewVersion < TB3O.version) {
						alert(T('BETAVERSION') + ' (v ' + TB3O.version + ') ?!');
						return;
					} else {
						if (window.confirm(T('NEWVERSIONAV') + ' (v ' + theNewVersion + ')!\n\n' + T('UPDATESCRIPT') + '\n')) window.location.href = TB3O.url;
					}
				}
			});
		} catch (ex) {}
	}
