	function formatTime(sec, returndays){
		if (sec > -1) {
			var h = Math.floor(sec/3600);
			var m = Math.floor(sec/60) % 60;
			var s = parseInt(sec % 60);
			var ht = "";
			if (returndays) {
				var d = Math.floor(h/24);
				h = h - d * 24;
				ht += d + ", ";
			}
			ht += h + ":" + convertTo2DigitString(m) + ":" + convertTo2DigitString(s);
		} else ht = "0:00:0?";
		return ht;
	}
	
