		if (aRatio < 1.00) aColor = ['red', '#FFE1E1']; else if (aRatio > 1.00) aColor = ['darkgreen', '#C8FFC8'];
		var ratioCell = newCell(aRatio.toFixed(2), [["style", "font-size:9px; background-color:" + aColor[1] + "; color:" + aColor[0] + ";"]]);
