//AUTO HUNT FEATURE
function Play()
{
	var footer = document.getElementsByClassName('build_version')[0];
	var txt = "color: #EDDBAF;font-size: 11px;"
	var MythM_ =
	'<div style="position: absolute; top: 85px; left: 225px; width: 167px; text-align: left;">' +
		'<div><font style="' + txt + '">Messages:</font><br/><font id="MythM_Info" style="color: #EDDBAF;"></font></div><br/><br/>' +
		'<div><font style="' + txt + '">Next Auto:</font><br/><font id="MythM_Time" style="color: #EDDBAF;">00:00</font></div>' +
	'</div><hr/><br/>';
	footer.innerHTML = MythM_ + footer.innerHTML;

	var MythM_ElementInfo = document.getElementById('MythM_Info');
	var MythM_ElementTime = document.getElementById('MythM_Time');
	var MythM_RemainingTime = -1;
	var MythM_TimeOut = -1;
	var MythM_Minute = '00';
	var MythM_Second = '00';
	var MythM_Counter = 0;
	var MythM_Timer = 0;
	var timerWordIndex = document.body.innerHTML.indexOf('a79378246206_remainingActiveTurnWaitSeconds = ')
	var wordlength = 'a79378246206_remainingActiveTurnWaitSeconds = '.length
	var timerIndex = (timerWordIndex + wordlength)
	var Mythtimer = parseFloat(document.body.innerHTML.substring(timerIndex, (timerIndex + 6)))
	autoTurn();


	function autoTurn() {
		if ((document.getElementById("captcha_session") != null) || (document.getElementsByClassName('overbar-r')[0].innerHTML.indexOf('x0') != -1)) {
			MythM_ElementTime.innerHTML = '<font style="color: #EDDBAF;">NOT RUNNING (PUZZLE / NO CARD)</font>';
			alert('Puzzle/No Challenge Card!');
			return;
		}

		MythM_RemainingTime = unsafeWindow.a79378246206_remainingActiveTurnWaitSeconds;
		// some null usually occurs, due it's generated on the onReload stuff... oh well...
		if (Mythtimer == null) {
			MythM_ElementInfo.innerHTML = '<font style="color: #EDDBAF;">timer is null!<br />going to retry again in 5 seconds - tries #' + MythM_Counter + ' (will retry 5x)</font>';
			MythM_Counter++;

			if (MythM_Counter == 6) {
				document.location = 'http://apps.facebook.com/mythmonger/';
			} else {
				setTimeout(doIt, 5000);
			}
			return;
		}

		MythM_ElementInfo.innerHTML = "";

		// set the time, add a random 30 seconds, so it will think you're the one who click
		MythM_TimeOut = (parseInt(Mythtimer) + Math.round(Math.random() * 10) + 5) * 1000;


		// the "click"
		setTimeout(function() { document.location = 'http://apps.facebook.com/mythmonger/turn.php'; }, MythM_TimeOut);

		// decoration, timer
		MythM_Timer = parseInt(MythM_TimeOut / 1000);

		// force refresh the time
		writeTime();
	}

	// this is just the decoration, it will show you how much time untill it will err... explode
	function writeTime() {
		MythM_Minute = parseInt(MythM_Timer / 60).toString();
		MythM_Second = parseInt(MythM_Timer - (MythM_Minute * 60)).toString();

		if (MythM_Minute.length == 1) MythM_Minute = '0' + MythM_Minute;
		if (MythM_Second.length == 1) MythM_Second = '0' + MythM_Second;
		MythM_ElementTime.innerHTML = MythM_Minute + ':' + MythM_Second;

		// reduce time
		MythM_Timer -= 1;

		// repeat
		setTimeout(writeTime, 1000);
	}
}
