function addEvent(obj, eventName, func){
	if (obj.attachEvent)
	{
	obj.attachEvent("on" + eventName, func);
	}
	else if(obj.addEventListener)
	{
	obj.addEventListener(eventName, func, true);
	}
	else
	{
	obj["on" + eventName] = func;
	}
}

