var getJSON = function(jsonstr,key) {
	var index = jsonstr.indexOf(key);
	if(index == -1) {
		return '';
	}
	var startIndex = index + key.length + 3;
	var endIndex = jsonstr.indexOf('\"' , startIndex);
	if(endIndex == -1) {
		return '';
	}
	return jsonstr.substring(startIndex,endIndex);
};

