// -- Fix long shouts --------------------------------------------------------------------------------------------------
function fixShouts() {
	$('#shouts_container .shout').each(function() {
		$(this).css({'max-height': '140px', 'overflow': 'auto'});
	});
	
	$('#previouspageshout, #nextpageshout').live('mouseup', function() {
		window.setTimeout(function() {
			$('#shouts_container .shout').each(function() {
				$(this).css({'max-height': '140px', 'overflow': 'auto'});
			});
		}, 3000);
	});
}
