                    if(rslt.updateSeed){unsafeWindow.update_seed(rslt.updateSeed)};
                    actionLog("Riassegna","Da: " + cityname + " Verso : " + xcoord + ',' + ycoord + "    ->   "+uW.g_js_strings.commonstr.troops+" : " + totalsend);
