  doQueue : function (cityId, tut, gam, que, errMsg){
    var t = Tabs.Train;
    try {
      t.displayCityStats();
      if (errMsg){
        t.dispTrainStatus ('<font color=#550000><B>ERRORE : '+ errMsg +'</b></font><BR>');
        t.setBusy(false);
        return;
      }
      var cmd = que.shift();
      if (!cmd){
        t.dispTrainStatus ('<B>Truppe addestrate con successo.</b><BR>');
        t.setBusy(false);
        return;
      }
      if (cmd[0] == 'T'){
        t.dispTrainStatus ('in addestramento '+ cmd[2] +' '+  unsafeWindow.unitcost['unt'+cmd[1]][0] +' a '+ Cities.byID[cityId].name +' ('+ que.length +' slot(s) rimanenti<BR>');
        doTrain (cityId, tut, gam, cmd[1], cmd[2], 
          function(errMsg){
	           setTimeout(function (){Tabs.Train.doQueue(cityId, tut, gam, que, errMsg);}, (Math.random()*1000)+1000 );
          }
        );
      }
    } catch (err) {
      t.dispTrainStatus ('<font color=#550000>PROGRAME ERRORE : '+ err.message +'</font><BR>');
      t.setBusy(false);
    }
  },
