// ==UserScript==
// @name           Torrentz Search Addons v1
// @namespace      http://userscripts.org/users/ym
// @description    Adds direct links on Torrentz search results to popular websites like Google, IMDb, Gamespot, Softpedia,Last.fm, Metacritic, Open Subtitles,  Grooveshark, Clerkdogs, Bitsnoop, Isohunt, btjunkie.  Also adds magnet links.
// @include        http://torrentz.eu/*
// @include        http://torrentz.me/*
// @include        http://torrentz.com/*
// ==/UserScript==


function jQueryIsReady($) {

  GM_addStyle(
    "dl:hover {background-color: #FFFFDF !important;}"+
    ".bs2 {background-color: #FFFFDF; border-bottom: 2px solid #F5DFD6; color: #666666; display: none; margin-left: 300px; padding: 3px 0 3px 10px; position: absolute; text-align: left; width: 690px;}"+
    "dl:hover .bs2 {display:block} .dli{vertical-align: middle; padding: 1px 2px} .dll{display:inline;} .dli:hover{padding:0 2px; border-top:1px solid #AAAAAA;border-bottom:1px solid #AAAAAA;}"
  );





  $(".results dl").each(function() {
  //  $(this).addClass('bsfs');
      var hash = this.childNodes[0].childNodes[0].href.split("/")[3].toUpperCase();
      var aval = this.childNodes[0].childNodes[0].innerHTML.replace(/<[^<]+?>/g,"");
      var tags = this.childNodes[0].innerHTML.split(" Â» ")[1];
      var avalp = aval.replace(/\s/g,"+");
      var avalm;
      var adata = '<div class="bs2">Direct: ' +
        '<a href="http://torrage.com/torrent/'+hash+'.torrent" class="dll" title="Download torrent file directly from Torrage"><img src="data:image/jpeg;base64," border="0" class="dli"></a>'+
        '<a href="http://torcache.com/torrent/'+hash+'.torrent" class="dll" title="Download torrent file directly from Torcache"><img src="data:image/x-icon;base64,%3D%3D" border="0" class="dli"></a>'+
        '<a href="http://zoink.it/torrent/'+hash+'.torrent" class="dll" title="Download torrent file directly from Zoink.it"><img src="/img/zoink.gif" border="0" class="dli"></a>'+
        ' Magnet:<a href="magnet:?xt=urn:btih:'+hash+'&dn='+avalp+'" title="Magnet Link"  class="dll"><img src="data:image/png;base64,%3D%3D" border="0" class="dli"></a>'+

        ' More: <a href="http://bitsnoop.com/torrent/'+hash+'" title="Search Bitsnoop (hash)" target="bitsnoop" class="dll"><img src="/img/130.gif" border="0" class="dli"></a>'+
        '<a href="http://www.google.com/search?q='+avalp+'" title="Google" target="_blank"><img src="data:image/png;base64,%3D" border="0" class="dli"></a>'+
        '<a href="http://btjunkie.org/search?q='+avalp+'" title="btjunkie" target="_blank"><img src="/img/32.gif" border="0" class="dli"></a>'+
        '<a href="http://thepiratebay.org/search/'+avalp+'" title="ThePirateBay" target="_blank"><img src="/img/20.gif" border="0" class="dli"></a>'+
        '<a href="http://isohunt.com/torrents/?ihq='+avalp+'" title="IsoHunt target="_blank"><img" src="data:image/png;base64,%3D%3D" border="0" class="dli"></a>'+

        '';
     if(tags.search("movies") !== -1)  {
        avalm = /.+(?=\+[1-2]\d{3})/.exec(avalp);
      if(avalm==null) {
        avalm = avalp;
        }
        adata += ' &#187; Movies:<a href="http://www.imdb.com/find?s=all&q='+avalm+'" class="dll" title="IMDb" target="_blank"><img src="data:image/png;base64,%3D%3D" border="0" class="dli"></a>'+
                 '<a href="http://www.criticker.com/?st=all&g=Go&h='+avalm+'" title="Criticker" target="_blank"><img src="data:image/x-icon;base64,%3D" border="0" class="dli"></a>'+
                 '<a href="http://www.opensubtitles.org/en/search2/sublanguageid-eng/moviename-'+avalm+'" title="OpenSubtitles.net" target="_blank"><img src="data:image/x-icon;base64," border="0" class="dli"></a>'+
                 '<a href="http://take.fm/movies/search?query='+avalm+'" title="Take.fm" target="_blank"><img src="/img/4.gif" border="0" class="dli"></a>'+
                 '<a href="http://www.metacritic.com/search/movie/'+avalm+'/results" title="Metacritic" target="_blank"><img src="data:image/png;base64,%3D%3D" border="0" class="dli"></a>'+
                 '<a href="http://www.clerkdogs.com/movies/search?q='+avalm+'" title="Clerkdogs (movie recommendation engine)" target="_blank"><img src="data:image/x-icon;base64,%3D%3D" border="0" class="dli"></a>'+
                 '<a href="http://www.youtube.com/results?search_query='+avalm+'" title="YouTube" target="_blank"><img src="data:image/x-icon;base64,%3D%3D" border="0" class="dli"></a>'+
                 ''   ;
      }
     if(tags.search("tv") !== -1)  {
        if(/.+S\d{1,2}E\d{1,3}/i.exec(avalp)!==null) {avalp=/.+S\d{1,2}E\d{1,3}/i.exec(avalp);}
        adata += ' &#187; TV:<a href="http://www.imdb.com/find?s=all&q='+avalp+'" class="dll" title="IMDb" target="_blank"><img src="data:image/png;base64,%3D%3D" border="0" class="dli"></a>'+
                 '<a href="http://www.metacritic.com/search/tv/'+avalp+'/results" title="Metacritic" target="_blank"><img src="data:image/png;base64,%3D%3D" border="0" class="dli"></a>'+
                 '<a href="http://www.tvsubtitles.net/search.php?q='+avalp+'" title="TVsubtitles.net" target="_blank"><img src="data:text/plain;base64,%3D" border="0" class="dli"></a>'+
                 '<a href="http://www.tvsubs.net/setlang.php?page=/search.php?q='+avalp+'&setlang1=en" title="TVsubs" target="_blank"><img src="data:image/gif;base64,%3D" border="0" class="dli"></a>'+

        '';
        }
     if(tags.search("games") !== -1)  {
        adata += ' &#187; Games:'+
                 '<a href="http://www.metacritic.com/search/game/'+avalp+'/results" title="Metacritic Games" target="_blank"><img src="data:image/png;base64,%3D%3D" border="0" class="dli"></a>'+
                 '<a href="http://gamespot.com/search.html?qs='+avalp+'" title="Gamespot" target="_blank"><img src="data:image/png;base64,%3D%3D" border="0" class="dli"></a>'+

        '';
        }
    if(tags.search("music") !== -1)  {
        if(/(\b.+?\b\+\b.+?\b){2}/.exec(avalp)!==null) {avalm=/(\b.+?\b\+\b.+?\b){2}/.exec(avalp)[0];}
       else {avalm=avalp;}
         adata += ' &#187; Music:'+
        '<a href="http://www.last.fm/search?q='+avalm+'" title="Last.fm" target="_blank"><img src="data:image/x-icon;base64,%3D%3D" border="0" class="dli"></a>'+
        '<a href="http://coda.fm/albums/search?query='+avalm+'" title="Coda.fm" target="_blank"><img src="/img/6.gif" border="0" class="dli" ></a>'+
        '<a href="http://listen.grooveshark.com/#/search/song?q='+avalm+'" title="Grooveshark (free online streaming music)" target="groove"><img src="data:image/x-icon;base64,%3D%3D" border="0" class="dli"></a>'+
        '';
        }

     if(tags.search(/app[sz]|applications|software/) !== -1)  {
          if(/\b.+?\b\+\b.+?\b\+\b.+?\b/.exec(avalp)!==null) {avalm=/\b.+?\b\+\b.+?\b\+\b.+?\b/.exec(avalp)[0];}
       else {avalm=avalp;}
        adata += ' &#187; Apps:'+
        '<a href="http://www.softpedia.com/dyn-search.php?search_term='+avalm+'" title="Softpedia" target="_blank"><img src="data:image/png;base64," border="0" class="dli" ></a>'+
        '<a href="http://download.cnet.com/1770-20_4-0.html?platformSelect=&tag=srch&searchtype=downloads&filterName=platform%3DWindows%2CMobile%2CMac%2CWebware&filter=platform%3DWindows%2CMobile%2CMac%2CWebware&query='+avalm+'" title="Download.com" target="_blank"><img src="data:image/x-icon;base64,%3D%3D" border="0" class="dli" ></a>'+
        '<a href="http://www.filehippo.com/search?q='+avalm+'" title="Filehippo" target="_blank"><img src="data:image/x-icon;base64,%3D" border="0" class="dli" ></a>'+
        '<a href="http://www.dl4all.com/index.php?do=search&story='+avalm+'" title="Dl4all -full zip downloads" target="_blank"><img src="data:image/x-icon;base64,%3D%3D" border="0" class="dli" ></a>'+
        '<a href="http://www.google.com/search?q='+avalm+'" title="Google (clean keywords)" target="_blank"><img src="data:image/png;base64,%3D" border="0" class="dli"></a>'+


        '';
        }

       $(this).append(adata);



  });










}


