function getX(element)
{
   var e;
   var x = 0;
   for ( e = element; e; e = e.offsetParent ) 
   {
      x += e.offsetLeft; 
   }

   for ( e = element.parentNode; e && e !== document.body; e = e.parentNode )
   {
      x -= e.scrollLeft;
   }

   return x;
}

//////////////////////////////////////////////////////////////////////
function getY(element)
{
   var e;
   var y = 0;
   for ( e = element; e; e = e.offsetParent ) 
   {
      y += e.offsetTop; 
   }

   for ( e = element.parentNode; e && e !== document.body; e = e.parentNode )
   {
      y -= e.scrollTop;
   }

   return y;
}

