		var maxSlots = 0;
		maxSlots = (cpbuilding == 26)?((lvl==20)?3:(lvl>=15)?2:(lvl>=10)?1:0):(lvl==20)?2:(lvl>=10)?1:0;

		var expTable = $xf("//div[@id='" + ID_MID2 + "']//table[@id='expansion'] | //div[@id='" + ID_MID2 + "']//table[@class='tbg']", 'f', ad);
