    function findPos(obj) {
        var x = 0, y = 0;
        while (obj.offsetParent) {
            x += obj.offsetLeft;
            y += obj.offsetTop;
            obj = obj.offsetParent;
        }
        return [x,y];
    }


    function checkPopup(ev) {
        return;
        // Think about caching this div - should improve speed
        var div = document.getElementById('lj_userpopup');
        if (div) {
            pos = findPos(div);
            var thisWidth = pos[0] + div.offsetWidth;
            var thisHeight = pos[1] + div.offsetHeight;

            if (((ev.pageX <= pos[0]) || (ev.pageX >= thisWidth)) ||
                ((ev.clientY <= pos[1]) || (ev.clientY >= thisHeight))) {
                clearTimer();
                div.parentNode.removeChild(div);
            }
        }
        return true;
    }

    function clearTimer()
    {
        if (timer != null)
        {
            clearTimeout(timer);
            timer = null;
        }
    }

    function addPopup(name, id)
    {
        // We need this for speed and for avoiding auto-closing tags
        var popupText = '';
        try
        {
            var mainPopupSpanHTML = '<span style="padding:0;float:left; width:270px;" id="lj_userpopup_main">';
            var strId = name;
            strId = strId.replace(/^\s*/, "").replace(/\s*$/, "");

            popupText = '<h3 style="font-family:Helvetica,Arial,Sans-serif;font-size:14pt; margin:0px; padding:4px; color:black; background:white;"><b>'+strId+'</b></h3>';
            popupText += mainPopupSpanHTML;
            popupText += '<h4 style="font-family:Helvetica,Arial,Sans-serif;font-size:12pt; margin:0px; padding:4px; color:black; background:white;">'+words_meaning[strId]+'</h4>';
            popupText += '</span>';
        }
        catch(err)
        {
            allert(err);
        }

        var pos = findPos(document.getElementById(id));



        var css ="font:10px/12px Arial,Helvetica,sans-serif;"
        + "border:solid 1px #666666; width:270px; padding:1px;"
        + "position:absolute; z-index:2001;"
        + "visibility:hidden; color:#333333; top:"+(pos[1])+"px;"
        + "left:"+pos[0]+"px; background-color:#ffffcc;"
        + "layer-background-color:#ffffcc;";

        var div = document.createElement('div');
        div.id = 'lj_userpopup';
        div.style.cssText = css;
        //        div.style.cssText = 'border: 1px black solid; padding: 0px; background: Cornsilk; color: Black; position: fixed; z-index: 2001; left: ' +
        //        100 + 'px; top: ' + 100 + 'px; text-align: left;-moz-opacity:' +
        //        1 + ';';

        div.innerHTML = popupText;

        var oldPopup = document.getElementById('lj_userpopup');
        if (oldPopup) {
            oldPopup.parentNode.removeChild(oldPopup);
        }

        document.body.appendChild(div);
    }

    function mouseover(ev) {
        if (ev.ctrlKey) {
            return;
        }
        var pos = findPos(this);

        // We need this for speed and for avoiding auto-closing tags
        var popupText = '';
        try
        {
            var mainPopupSpanHTML = '<span style="padding:0;float:left; width:150px;" id="lj_userpopup_main">';
            var strId = ev["target"].id;
            strId = strId.replace(/^\s*/, "").replace(/\s*$/, "");

            popupText = '<h3 style="font-family:Helvetica,Arial,Sans-serif;font-size:14pt; margin:0px; padding:4px; color:black; background:white;"><b>'+strId+'</b></h3>';
            popupText += mainPopupSpanHTML;
            popupText += '<h4 style="font-family:Helvetica,Arial,Sans-serif;font-size:12pt; margin:0px; padding:4px; color:black; background:white;">'+words_meaning[strId]+'</h4>';
            popupText += '</span>';

        }
        catch(err)
        {
            allert(err);
        }

        // We show popup after delay
        timer = window.setTimeout(function() {
            var div = document.createElement('div');
            div.id = 'lj_userpopup';
            div.style.cssText = 'border: 1px black solid; padding: 0px; background: Cornsilk; color: Black; position: fixed; z-index: 2001; left: ' +
            (pos[0]) + 'px; top: ' + (pos[1] - 50) + 'px; text-align: left;-moz-opacity:' +
            1 + ';';

            div.innerHTML = popupText;
            var oldPopup = document.getElementById('lj_userpopup');
            if (oldPopup) {
                oldPopup.parentNode.removeChild(oldPopup);
            }
            document.body.appendChild(div);
            clearTimer();
        }, 500);
    }



