function addUser(server, user, password) {
	var tS = getVal("server");
	if ( tS ) tS = tS.split("|");
	else tS = new Array();
	
	var tU = getVal("user");
	if ( tU ) tU = tU.split("↨⌂₧☻");
	else tU = new Array();
	
	var tP = getVal("password");
	if ( tP ) tP = tP.split("↨⌂₧☻");
	else tP = new Array();
	
	var tA = getVal("active");
	if ( tA ) tA = tA.split("|");
	else tA = new Array();
	
	var tL = getVal("loggedin");
	if ( tL ) tL = tL.split("|");
	else tL = new Array();
	
	var i = tS.indexOf(server);
	
	if ( ifGM ) {
		if ( i < 0 ) {
			tS.push(server);
			tU.push(user);
			tP.push(password);
			tA.push(0);
			tL.push(2);
		} else {
			tU[i] = user;
			tP[i] = password;
			tL[i] = 2;
		}
	} else {
		tS[0] = server;
		tU[0] = user;
		tP[0] = password;
		tA[0] = 1;
		tL[0] = 2;
	}
	
	setVal("server", tS.join("|"));
	setVal("user", tU.join("↨⌂₧☻"));
	setVal("password", tP.join("↨⌂₧☻"));
	setVal("active", tA.join("|"));
	setVal("loggedin", tL.join("|"));
}

function logUser(inOut) {
	var tS = getVal("server");
	var tL = getVal("loggedin");
	if ( !tS || !tL || tS == "" || tL == "" ) return;
	tS = tS.split("|");
	tL = tL.split("|");
	var i = tS.indexOf(location.hostname);
	tL[i] = inOut;
	setVal("loggedin", tL.join("|"));
}

function getUser() {
	var s = getVal("server");
	var u = getVal("user");
	var p = getVal("password");
	var a = getVal("active");
	var l = getVal("loggedin");
	if ( !s || !u || !p || !a || !l || s == "" || u == "" || p == "" || a == "" || l == "" ) return [null,null,null];
	s = s.split("|");
	u = u.split("↨⌂₧☻");
	p = p.split("↨⌂₧☻");
	a = a.split("|");
	l = l.split("|");
	len = s.length;
	if ( len < 1 || u.length != len || p.length != len || a.length != len || l.length != len ) return [null,null,null];
	for ( var i = 0 ; i < len ; ++i ) if ( parseInt(a[i]) == 1 && (!ifGM || parseInt(l[i]) == 0) ) return [s[i],u[i],p[i]];
	return [null,null,null];
}

function oaHider ( evt ) {
	var buttons = document.getElementsByName("oaButton");
	if ( buttons[0].style.display == "none" ) {
		setValS("HideMenu", 0);
		for ( var i = 0, k = buttons.length ; i < k ; ++i ) buttons[i].style.display="inline";
	} else {
		setValS("HideMenu", 1);
		for ( var i = 0, k = buttons.length ; i < k ; ++i ) buttons[i].style.display="none";
	}
}

function oaUnload () {
	for ( var p in theListeners ) {
		if (p == 0) {
			theListeners[p].firstChild.nextSibling.removeEventListener("click", oaFlipper, false);
			theListeners[p].firstChild.removeEventListener("click", oaHider, false);
		} else theListeners[p].firstChild.removeEventListener("click", oaFlipper, false);
	}
	
	var tmp = $id("oaEmailTest");
	if ( tmp ) tmp.removeEventListener("click",oaSendTestMail,false);
	tmp = $id("oaSaveOpts");
	if ( tmp ) tmp.removeEventListener("click",oaSaveCloseOpts,false);
	tmp = $id("oaLanguage");
	if ( tmp ) tmp.removeEventListener("change",oaChangeLang,false);
	tmp = $id("oaCloseOptsDiv");
	if ( tmp ) tmp.removeEventListener("click",oaCloseOpts,false);
	tmp = $id("oaOptsTitle");
	if ( tmp ) tmp.removeEventListener("mousedown", oaDragObjDown, false);
	tmp = $id("oaReset");
	if ( tmp ) 	tmp.removeEventListener("click",oaPromptReset,false);
	
	tmp = document.getElementsByName("oaOpt");
	for ( var p in tmp ) if ( tmp[p].removeEventListener ) tmp[p].removeEventListener("click", oaShowOptionsBox, false);
	clearTimeout(oaAlarmID);
	clearTimeout(oaReloadID);
	window.removeEventListener("load", oaLoad, false);
	window.removeEventListener("unload", oaUnload, false);
	document.removeEventListener("mousemove", oaMoveDiv, false);
	document.removeEventListener("mouseup", oaCancelMoveDiv, false);
	logUser(0);
}

function oaLoad () {
	var t = $id("ui-dialog-title-1");
	if (t) {
		t = t.nextSibling;
		if (t) bLog("load > t is " + t.nextSibling);
		else bLog("load > found dialog, but no close button");
	} else bLog("load > No dialog to close");
	var tOnOffs = setOnOff(0,true).split(",");
	for ( var i = 0 ; i < 6 ; ++i ) tOnOffs[i] = (tOnOffs[i] == "false" || tOnOffs[i] == false) ? false : true;

	var leftMenu = $id("menuTable");
	if ( leftMenu == null ) {
		bLog("load > Left Menu not found!");
	} else { 
		bLog("load > Adding to Left Menu.");
		var tLI;
		var hide = getValS("HideMenu", 0);
		for ( var i = 0 ; i < 6 ; ++i ) {
			tLI = document.createElement("li");
			tLI.innerHTML = '<span class="menu_icon" style="cursor:pointer;"">'+(i==0?'<span style="position:absolute;left:-12px;margin:3px 0px 0px 0px;font-size:'+oaHideBulletSize+';color:grey;" title="Hide/Unhide OGAwC Buttons" onclick="if (this.innerHTML==\''+oaHideBulletOpen+'\')this.innerHTML=\''+oaHideBulletClosed+'\';else this.innerHTML=\''+oaHideBulletOpen+'\';" onmouseover="this.style.color=\'white\';" onmouseout="this.style.color=\'grey\';">'+(hide?oaHideBulletClosed:oaHideBulletOpen)+'</span>':'')+'<img id="'+i+'oaOnOff" src="' + (tOnOffs[i] ? onPic : offPic) + '"></span><a class="menubutton" href="#" accesskey="" target="" name="oaOpt"><span class="textlabel" onclick="return false;">'+aLang[i]+'</span></a>';
			if ( i == 0 ) {
				tLI.firstChild.firstChild.nextSibling.addEventListener("click", oaFlipper, false);
				tLI.firstChild.firstChild.addEventListener("click", oaHider, false);
			} else {
				tLI.setAttribute("name","oaButton");
				if ( hide ) tLI.setAttribute("style","display:none;");
				tLI.firstChild.firstChild.addEventListener("click", oaFlipper, false);
			}
			if ( i == 5 && ifGM ) tLI.children[1].addEventListener("click", oaShowAutoLoginOptionsBox, false);
			else tLI.children[1].addEventListener("click", oaShowOptionsBox, false);
			theListeners[i] = tLI.firstChild;
			leftMenu.appendChild(tLI);
		}
		tLI = document.createElement("div");
		tLI.setAttribute("style", "font-size:10px");
		tLI.innerHTML = aLang[21]+"<br>"+oaVersion;
		leftMenu.appendChild(tLI);
	}
}
