bLog("preload > location = " + location);
if ( location.search == "" && location.pathname == "/" ) {
	var g = getUser();
	var s = g[0];
	var u = g[1];
	var p = g[2];
	if ( s != null ) {
		bLog("preload > Autologging in now...> "+s+", "+u);
		setTimeout("document.getElementById('serverLogin').value = '"+s+"';document.getElementById('usernameLogin').value = '"+u+"';  document.getElementById('passwordLogin').value = '"+p+"'; document.getElementById('loginForm').submit();", oaRandomNumber()*2);
	} else {
		var n = $id("usernameLogin");
		bLog("n>"+n);
		if (n) n = n.value;
		bLog("n>"+n);
		var l = $id("loginForm");
		if ( n && l && n != "" && n.length > 1 ) {
			bLog("preload > Saved login data is null. Attempting AutoLogin from auto-fill.");
			l.submit();
		} else bLog("preload > Saved login data is null. Not autologging in.");
		bLog("user>"+getUser());
	}
	window.addEventListener("unload", oaSaveLogin, false);
	oaReloadID = setTimeout("window.location.reload();",180000);
} else if ( location.pathname.indexOf("loginError") > 0 ) {
	var s = getVal("server");
	var u = getVal("user");
	var p = getVal("password");
	var a = getVal("active");
	var l = getVal("loggedin");

	if ( s && u && p && a && l && s != "" && u != "" && p != "" && a != "" && l != "" ) {
		s = s.split("|");
		u = u.split("↨⌂₧☻");
		p = p.split("↨⌂₧☻");
		a = a.split("|");
		l = l.split("|");
		
		for (var i = 0 ; i < l.length ; ++i ) {
			if ( parseInt(l[i]) == 2 ) {
				s.splice(i,1);
				u.splice(i,1);
				p.splice(i,1);
				a.splice(i,1);
				l.splice(i,1);
				--i;
			}
		}
		setVal("server", s.join("|"));
		setVal("user", u.join("↨⌂₧☻"));
		setVal("password", p.join("↨⌂₧☻"));
		setVal("active", a.join("|"));
		setVal("loggedin", l.join("|"));
	}
	window.addEventListener("unload", oaSaveLogin, false);
} else {
	bLog("preload > OGAwC Loading... Load Count = "+setVal("loadcount", parseInt(getVal("loadcount",0))+1 )+", "+ifGM );
	logUser(1);
	oaSetServer();
	setLanguage(getValS("lang",theLang));
	if ( ifGM ) GM_registerMenuCommand(aLang[33], oaPromptReset);
	MIN = parseInt(getValS("MIN",MIN));
	MAX = parseInt(getValS("MAX",MAX));
	if ( MIN < 100 ) MIN = 100;
	if ( MAX < 200 ) MAX = 200;
	AlarmCheckPeriod = parseInt(getValS("oaAlarmCheckPeriod",AlarmCheckPeriod));
	AlarmRepeatPeriod = parseInt(getValS("oaAlarmRepeatPeriod",AlarmRepeatPeriod));
	oaReloadID = setTimeout(oaReload,oaRandomNumber()*1000);
	oaAlarmID = setTimeout(oaAlarmCheck, ((AlarmCheckPeriod*1000) + (oaRandomNumber()*2)) );

	window.addEventListener("unload", oaUnload, false);
	if ( ifGM ) window.addEventListener("load", oaLoad, false);
	else oaLoad(); // Because chrome runs scripts at window Idle. Which is sometimes *after* window load. But thankfully, here, its always after the DOM is loaded, confusing, I know.
}

