	String.prototype.hasWords=function(sip){
	sip=sip.replace(/[.,;]/g,"")
	var rate=0
	var has=0
	var ws=mtrim(sip).split(" ")
	for(var i=0;i<ws.length;i++){
		var idx=this.indexOf(ws[i])
		if(idx>-1){
			has++
		}
	}
	rate=has/ws.length
	return rate
	}
	
	mtrim=function(s){
		s=s.replace(/\n/ig,"")
		while(s.indexOf("  ")!==-1){
			s=s.replace(/  /ig," ")
		}
		s=s.replace(/\. /ig,".")
		s=s.replace(/^ /,"")
		s=s.replace(/ $/,"")
		return s
	}
