var Outil = {
	RetQuantum : function (frequency, TotRes) {
		[]
		var quant = 1;
		if (frequency < 500) {
			quant = Math.round(500 / frequency);
			frequency *= quant;
		}
		if (TotRes > 0)
			quant = -quant;

		return [frequency, quant];
	},
	bind : function (f, boundArgs) {
		return function () {
			return f.apply(this, boundArgs);
		};
	},
	CalculTempsTroupe : function (x1, y1, x2, y2, Vit) {
		var dX = Math.min(Math.abs(x2 - x1), Math.abs(801 - Math.abs(x2 - x1)));
		var dY = Math.min(Math.abs(y2 - y1), Math.abs(801 - Math.abs(y2 - y1)));
		var dist = Math.sqrt(Math.pow(dX, 2) + Math.pow(dY, 2));
		return this.formatTimeSpan(dist * 3600 / Vit);
	},
	CalculTempsProd : function (x) {
		var Tmp;
		if (Vi.Prod(x) > -1) {
			Tmp = Math.floor(((Vi.Capa(x) - Vi.RessRestant(x)) / Vi.Prod(x)) * 3600);
		} else {
			Tmp = Math.floor(-1 * (Vi.RessRestant(x) / Vi.Prod(x)) * 3600);
		}
		return Tmp;
	},
	IsPossibleUPG : function (gid, Niv) {
		for (var i = 0; i < 4; i++) {
			if (Vi.RessRestant(i) < GidInfo[gid][Niv][i]) {
				return (Vi.TotalRestant() > GidInfo[gid][Niv][4] ? 1 : 2);
			}
		}
		return 0;
	},
	formatTimeSpan : function (sec) {
		if (this.isIntValid(sec) && sec >= 0) {
			h = Math.floor(sec / 3600);
			m = Math.floor(sec / 60) % 60;
			s = parseInt(sec % 60);
			return (h > 9 ? h : '0' + h) + ":" + (m > 9 ? m : '0' + m) + ":" + (s > 9 ? s : '0' + s);
		} else {
			return "-";
		}
	},
	FormatMillier : function (nStr) {
		nStr += '';
		x = nStr.split('.');
		x1 = x[0];
		x2 = x.length > 1 ? '.' + x[1] : '';
		var rgx = /(\d+)(\d{3})/;
		while (rgx.test(x1)) {
			x1 = x1.replace(rgx, '$1' + ' ' + '$2');
		};
		return x1 + x2;
	},
	parseInt10 : function (str, defv) {
		var v = parseInt(str, 10);
		if (isNaN(v) && defv !== undefined) {
			v = defv;
		}
		return v;
	},
	isIntValid : function (x) {
		return x !== null && isFinite(x);
	},
	toSeconds : function (x) {
		var p = x.split(":");
		return (p.length === 3) ? (p[0] * 3600) + (p[1] * 60) + (p[2] * 1) : Number.NaN;
	},
	removeInvisibleChars : function (str) {
		return str.replace(/[\u200E\u200F\u202A\u202B\u202C\u202D\u202E]/g, '');
	},
	decodeHTML : function (str) {
		if (!this.decodeHTML.element) {
			this.decodeHTML.element = document.createElement('div');
		}
		this.decodeHTML.element.innerHTML = str;
		str = this.removeInvisibleChars(this.decodeHTML.element.innerHTML);
		this.decodeHTML.element.textContent = '';
		return str;
	}
};
