		var aColor = 'black';
		var bColor = 'white';
		var bColor;
		if (aRatio < 1.00) {
			aColor = 'red';
			bColor = '#FFE1E1';
		} else if (aRatio > 1.00) {
			aColor = 'darkgreen';
			bColor = '#C8FFC8';
		}
