	function getTroopsAttDefInfoTable(tNTroops, boolFromMap, boolAddMinInfo) {
		//log(3, "animalsTable = " + tNTroops);
		if (!tNTroops) return '';
		var tNinfo = [0, 0, 0, 0];
		var toolTipHTML = '';
		var boolFoundInfo = false;
		var strMinInfo = '';
		var strMinX = '';
		if (tNTroops.rows == null) return toolTipHTML;
		for (var i = 0; i < tNTroops.rows.length; i++) {
			var aRow = tNTroops.rows[i];
			var aImg = aRow.cells[0].firstChild;
			if (aImg.nodeName != 'IMG') aImg = aRow.cells[0].firstChild.firstChild;
			if (aImg != null && aImg.src) {
				var index = getTroopIndexTitleFromImage(aImg)[0];
				if (!isNaN(index) && index > 0) {
					if (boolFromMap) {
						var imgHTML = '<img class="' + aImg.className + '" src="' + xGIF + '">';
						if (TB3O.boolIsT35 == false) imgHTML = "<img src=" + aRow.childNodes[0].firstChild.src + ">";
						toolTipHTML += "<td>" + imgHTML + "</td><td align='" + docDir[1] + "'>" + aRow.cells[1].textContent + "</td></tr>";
					}
					var tNo = parseInt(aRow.cells[1].textContent);
					tNinfo[0] += tNo * uc[index][5];
					tNinfo[1] += tNo * uc[index][6];
					tNinfo[2] += tNo * uc[index][7];
					tNinfo[3] += tNo * uc[index][9];
					boolFoundInfo = true;
				} else {
					tNinfo[3] += 6; //hero
				}
			} else {
				//if (boolFromMap) toolTipHTML += "<tr><td>" + aRow.cells[0].textContent + "</td></tr>";
				toolTipHTML += "<tr><td>" + aRow.cells[0].textContent + "</td></tr>";
			}
			//toolTipHTML += "</tr>";
		}
		if (boolFoundInfo == true) {
			if (tNinfo[0] != 0 + tNinfo[1] + tNinfo[2] > 0) {
				var strTextAlign = docDir[1];
				if (boolFromMap == true) {
					toolTipHTML += "<tr><td>&nbsp;</td></tr>";
					strTextAlign = docDir[1];
				}
				if (boolAddMinInfo == true) {
					strMinInfo = "* = " + T('MIN');
					strMinX = "*";
					toolTipHTML += "<tr><td colspan='2' style='font-weight:bold; text-align:center;'>" + T('STATISTICS') + "</td></tr>";
				}
				toolTipHTML += "<tr><td>" + gIcons["att_all"] + strMinX + "</td><td align='" + docDir[1] + "'" + " style='text-align:" + strTextAlign + ";'>&nbsp;" + tNinfo[0].toLocaleString(2) + "</td></tr>";
				toolTipHTML += "<tr><td>" + gIcons["def_i"] + strMinX + "</td><td align='" + docDir[1] + "'" + " style='text-align:" + strTextAlign + ";'>&nbsp;" + tNinfo[1].toLocaleString(2) + "</td></tr>";
				toolTipHTML += "<tr><td>" + gIcons["def_c"] + strMinX + "</td><td align='" + docDir[1] + "'" + " style='text-align:" + strTextAlign + ";'>&nbsp;" + tNinfo[2].toLocaleString(2) + "</td></tr>";
				toolTipHTML += "<tr><td>" + gIcons["r5"] + "</td><td align='" + docDir[1] + "'" + " style='text-align:" + strTextAlign + ";'>&nbsp;" + tNinfo[3].toLocaleString(2) + "</td></tr>";
				if (boolAddMinInfo == true) toolTipHTML += "<tr><td colspan='2' style='font-size:8pt;'>" + strMinInfo + "</td></tr>";
			}
		}
		return toolTipHTML;
	}
	
