	function getdorf3SelectedVinfo(ansdoc, ans) {
		// newdid of the village
		var newdid;
		var vID = 0;
		var retValue = [-1000, -1000];
		try {
			if (TB3O.boolIsT35 == false) {
				ansdoc.evaluate("//a[@class='active_vl']", ans, null, XPFirst, null).singleNodeValue.getAttribute("href").search(/\?newdid=(\d+)/);
				newdid = RegExp.$1;
				
				var aX = ansdoc.evaluate('//a[@class="active_vl"]/../../td/table/tbody/tr/td', ans, null, XPFirst, null).singleNodeValue;
				if (aX) {
					var X = parseInt(aX.innerHTML.replace("(", ""));
					var aY = ansdoc.evaluate('//a[@class="active_vl"]/../../td/table/tbody/tr/td[3]', ans, null, XPFirst, null).singleNodeValue;
					if (aY) {
						var Y = parseInt(aY.innerHTML.replace(")", ""));
						vID = xy2id(X, Y);
					}
				}
			} else {
				//log(3, "=> getdorf3SelectedVInfo: entering evaluate procedure"); 
				var newdidLink = ansdoc.evaluate("//div[@id='vlist']//table[@class='vlist']//tr[@class='sel']//a", ans, null, XPFirst, null).singleNodeValue;
				//log(3, "=> getdorf3SelectedVInfo: newdidLink = " + newdidLink);
				newdid = getNewdidFromLink(newdidLink.href);
				var vx = newdidLink.parentNode.parentNode.cells[2].textContent.replace("(", "");
				var vy = newdidLink.parentNode.parentNode.cells[4].textContent.replace(")", "");
				vID = xy2id(vx, vy);
			}
		} catch(e) {
			newdid = aVillage.vNewdid;
			vID = aVillage.vID;
		}
		retValue[0] = vID;
		retValue[1] = newdid;
		return retValue;
	}
	
