			for (var i in bugfix)
				bugfix[i] = bugfix[i].replace(/\/\/\s+@bugfix\s+/, "");
			recovery.datas.update.newver = newver;
			recovery.datas.update.news = news;
			recovery.datas.update.bugfix = bugfix;
