// ==UserScript==
// @name        HV领便当助手
// @namespace   bluedust
// @description 教你正确的领便当姿势
// @include     http://hentaiverse.org/*
// @exclude     http://hentaiverse.org/pages*
// @version     1
// @grant       none
// ==/UserScript==

//You have been defeated.
//存储对象：
/*{
 难度1:{
 怪物1：{
 等级：100,
 伤害值：9000,
 伤害比值：0.9，
 伤害类型：'crushing',
 次数：3
 }
 }
 }*/

(function(){
    var warningImage = 'data:image/png;base64,';
//    var dangerImage = 'data:image/png;base64,';
    var dangerImage = 'data:image/png;base64,';

    var nameSpace = 'bento_assistant';
    var applicationName = 'HV便当助手';
    var L = localStorageData();
    var damagePopWin = null;

    function localStorageSet(key, value){
        L[key] = value;
        localStorage[nameSpace] = JSON.stringify(L);
    }

    function localStorageGet(key){
        return L[key];
    }

    function clearLocalStorage(){
        localStorage[nameSpace] = '{}';
    }

    function localStorageData(){
        return JSON.parse(localStorage[nameSpace] || '{}');
    }

    var config = localStorageGet('config') || {};
    var warningRunMode = config.warningRunMode || {};

    var difficulty = document.querySelectorAll('.clb .cit')[1].querySelectorAll('.fd4 div')[1].innerHTML;
    var healthThreshold = localStorageGet('healthThreshold') || 0.6;
    var playerMaxHealth = parseInt(document.querySelector('.clb .cwbdv .cwbt .fd2 div').innerHTML.split('/')[1]);
    var difficultyDamageData = localStorageGet(difficulty) || {};
    var monsters = null;

    if(!document.getElementById("togpane_log")){
        if(/http:\/\/hentaiverse.org\/\?s=Battle.*/i.test(document.location.href)){
            showConfigUI();
            return;
        }
        else{
            return;
        }
    }

    checkBattleInfo();
    if(isRunWarning())showWarning();
    checkDead();
    showBattleInfo();


    function getMonsters(){
        var monsters = [];
        var monsterElems = document.querySelectorAll('#monsterpane .btm1');

        for(var key = 0; key < monsterElems.length; key++){
            var healthElem = monsterElems[key].querySelector('.btm4 .btm5').querySelector('.chbd .chb2');
            if(healthElem == null){
                monsters.push(null);
            }
            else {
                var monsterName = monsterElems[key].querySelector('.btm3 .fd2 div').innerHTML;
                var spirit = monsterElems[key].querySelectorAll('.btm4 .btm5')[2].querySelector('img').style.width;
                spirit = parseInt(spirit.substr(0, spirit.length -2));
                monsters.push({
                    name: monsterName,
                    spirit: spirit
                });
            }
        }
        return monsters;
    }

    function isRunWarning(){
        if(/http:\/\/hentaiverse.org\/\?s=Battle&ss=iw.*/i.test(document.location.href)){
            return typeof warningRunMode.IW == 'undefined' ? true : warningRunMode.IW;
        }

        if(/http:\/\/hentaiverse.org\/\?s=Battle&ss=gr.*/i.test(document.location.href)){
            return typeof warningRunMode.GF == 'undefined' ? true : warningRunMode.GF;
        }

        if(/http:\/\/hentaiverse.org\/\?s=Battle&ss=ar.*/i.test(document.location.href)){
            return typeof warningRunMode.JJC == 'undefined' ? true : warningRunMode.JJC;
        }

        if(/http:\/\/hentaiverse.org\/\?s=Battle&ss=rb.*/i.test(document.location.href)){
            return typeof warningRunMode.ROB == 'undefined' ? true : warningRunMode.ROB;
        }

        return true;
    }

    function setWarningRunMode(modeName, mode){
        warningRunMode[modeName] = mode;
        localStorageSet('config', config);
    }

    function getMonsterLevel(monsterName){
        var monsterElems = document.querySelectorAll('#monsterpane .btm1');
        for(var key = 0; key < monsterElems.length; key++){
            if(monsterName == monsterElems[key].querySelector('.btm3 .fd2 div').innerHTML){
                return monsterElems[key].querySelector('.btm2 .fd4 div').innerHTML;
            }
        }
    }

    function putMonsterDamage(monsterName, damageData){
        if(!difficultyDamageData[monsterName] || difficultyDamageData[monsterName].damage < damageData.damage)
        {
            difficultyDamageData[monsterName] = damageData;
            localStorageSet(difficulty, difficultyDamageData);
        }
    }

    function checkBattleInfo(){
        monsters = getMonsters();

        var battleLogBox = document.querySelector('#togpane_log');
        var roundNum = battleLogBox.querySelector('table tbody tr td').innerHTML;
        var pattern = /you for (\d+) (\w+) damage/i;
        var demarcationHealth = parseInt(playerMaxHealth * healthThreshold);

        var battleLogs = battleLogBox.querySelectorAll('table tbody tr');

        for(var key = 0; key < battleLogs.length; key++){
            var tds = battleLogs[key].getElementsByTagName('td');
            if(tds[0].innerHTML != roundNum)break;
            var battleLog = tds[2].innerHTML;

            var roundMatch = /\(Round\s+(\d+)\s*\/\s*(\d+)\)/i.exec(battleLog);
            if(roundMatch){
                localStorageSet('currRound', roundMatch[1]);
                localStorageSet('totalRounds', roundMatch[2]);
            }

            if(isRunWarning()){
                var damageMatch = pattern.exec(battleLog);
                if(damageMatch && parseInt(damageMatch[1]) >= demarcationHealth){
                    var monsterName;
                    if(!(monsterName = /^(.+) uses/i.exec(battleLog))){
                        monsterName = /^(.+) (hits|crits)/i.exec(battleLog)
                    }
                    monsterName = monsterName[1];

                    putMonsterDamage(monsterName, {
                        monsterLevel: getMonsterLevel(monsterName),
                        damageType: damageMatch[2],
                        damage: parseInt(damageMatch[1]),
                        damageProportion: parseInt(parseInt(damageMatch[1]) / playerMaxHealth * 100) + '%',
                        time: new Date().getTime()
                    });
                }
            }
        }
    }

    function checkDead(){
        var deadDiv = document.querySelector('#mainpane .btt .btcp .btc .fd4 div');
        if(deadDiv && deadDiv.innerHTML == 'You have been defeated!'){
            difficultyDamageData.deadRound = localStorageGet('currRound');
            difficultyDamageData.deadTotalRounds = localStorageGet('totalRounds');
            difficultyDamageData.deadMonsters = monsters.length;
            localStorageSet(difficulty, difficultyDamageData);
        }
    }

    function reloadPage(){
        document.location.href = document.location.href;
    }

    function showWarning(){
        var monsterElems = document.querySelectorAll('#monsterpane .btm1');

        for(var key in monsters){
            if(monsters[key] && difficultyDamageData[monsters[key].name]){
                var image = document.createElement('img');
                image.style.margin = '-2px'
                image.style.width = '36px';
                image.style.height = '36px';
                if(monsters[key].spirit == 120){
                    image.src = dangerImage;
                }
                else{
                    image.src = warningImage;
                }
                image.addEventListener('mouseenter', (function(monsterName){
                    return function(event){
                        showDamageInfo(difficultyDamageData[monsterName], this);
                    };
                })(monsters[key].name));

                image.addEventListener('mouseleave', function(event){
                    hideDamageInfo();
                });

                monsterElems[key].querySelector('.btm6').insertBefore(image, monsterElems[key].querySelector('.btm6').childNodes[0]);

            }
        }
    }

    function showBattleInfo(){
        new BattleInfoUI({
            round: localStorageGet('currRound') || 0,
            totalRounds: localStorageGet('totalRounds') || 0,
            deadRound: difficultyDamageData.deadRound || '无',
            deadTotalRounds: difficultyDamageData.deadTotalRounds || '无',
            deadMonsters: difficultyDamageData.deadMonsters || '无'
        }).show();
    }

    function showDamageInfo(damageInfo, imgElement){
        if(!damagePopWin) damagePopWin = new DamageInofUI();

        damagePopWin.show(635, getAbsoluteTop(imgElement.parentNode.parentNode), damageInfo);
    }

    function hideDamageInfo(){
        if(damagePopWin)damagePopWin.hide();
    }

    function showConfigUI(){
        new ConfigUI().show();
    }

    function ConfigUI(){
        var UIHandler;
        var timeText;
        var damageText;
        var damageProportionText;
        var damageTypeText;
        var monsterLevelText;

        this.initialize = function(){
            this.createUI();
        }

        this.createUI = function(){
            var boxDiv = document.createElement('div');
            boxDiv.style.boxShadow = '-1px 1px 3px 3px #666';
            boxDiv.style.height = 'auto';
            boxDiv.style.width = 'auto';
            boxDiv.style.position = 'absolute';
            boxDiv.style.zIndex = 1000;
            boxDiv.style.left = localStorageGet('configPanelLeft') || '1245px';
            boxDiv.style.top = localStorageGet('configPanelTop') || '200px'
            boxDiv.style.display = 'none';
            boxDiv.style.textAlign = 'left';

            var maskDiv = document.createElement('div');
            maskDiv.style.background = 'white';
            maskDiv.style.width = '182px';
            maskDiv.style.height = '255px';
            boxDiv.appendChild(maskDiv);

            var titleDiv = document.createElement('div');
            titleDiv.style.marginTop = '-250px';
            titleDiv.style.height = '17px';
            titleDiv.style.lineHeight = '18px';
            titleDiv.style.textAlign = 'center';
            titleDiv.style.fontWeight = 'bold';
            titleDiv.style.fontsize = '15px';
            titleDiv.style.cursor = 'move';
            titleDiv.innerHTML = applicationName + '-配置';
            boxDiv.appendChild(titleDiv);

            var innerBoxDiv = document.createElement('div');
            innerBoxDiv.style.margin = '5px';
            innerBoxDiv.style.background = 'RGB(240,240,240)';
            innerBoxDiv.style.border = '1px solid RGB(152,152,152)';
            innerBoxDiv.style.padding = '10px';
            innerBoxDiv.style.width = '150px';
            innerBoxDiv.style.height = '200px';
            boxDiv.appendChild(innerBoxDiv);

            innerBoxDiv.innerHTML = '生命阀值：<input type="text" id="hv_ba_config" value="'+parseInt(healthThreshold*100)
                +'" style="width:30px;">%' +
                '<div style="padding-top: 10px; padding-bottom: 5px;">骷髅头警告模式：</div>'+
                '<label style="width: 50%; float: left; display: block;"><input id="chkbox_jjc" type="checkbox">JJC</label>'+
                '<label style="float: left; display: block;"><input id="chkbox_rob" type="checkbox">ROB</label><br/>'+
                '<label style="width: 50%; float: left; display: block;"><input id="chkbox_gf" type="checkbox">GF</label>'+
                '<label style="float: left; display: block;"><input id="chkbox_iw" type="checkbox">IW</label>'+
                '<div style="text-align: center; padding-top: 70px; clear: both;"><br/>'+
                '<input id="configApply" type="button" value="应用">'+
                '<input id="configReset" type="button" style="margin-left: 40px;" value="重置"></div>';

            var jjcChkBox = innerBoxDiv.querySelector('#chkbox_jjc');
            var robChkBox = innerBoxDiv.querySelector('#chkbox_rob');
            var gfChkBox = innerBoxDiv.querySelector('#chkbox_gf');
            var iwChkBox = innerBoxDiv.querySelector('#chkbox_iw');

            if(typeof warningRunMode.JJC == 'undefined' || warningRunMode.JJC) jjcChkBox.checked = true;
            if(typeof warningRunMode.ROB == 'undefined' || warningRunMode.ROB) robChkBox.checked = true;
            if(typeof warningRunMode.GF == 'undefined' || warningRunMode.GF) gfChkBox.checked = true;
            if(typeof warningRunMode.IW == 'undefined' || warningRunMode.IW) iwChkBox.checked = true;

            innerBoxDiv.querySelector('#configApply').addEventListener('click', function(){
                var healthThresholdValue = parseInt(innerBoxDiv.querySelector('#hv_ba_config').value);
                if(healthThresholdValue >= 0 && healthThresholdValue < 100){
                    localStorageSet('healthThreshold', healthThresholdValue / 100);
                }
                else{
                    alert('数据错误！');
                    return;
                }

                warningRunMode.JJC = jjcChkBox.checked;
                warningRunMode.ROB = robChkBox.checked;
                warningRunMode.GF = gfChkBox.checked;
                warningRunMode.IW = iwChkBox.checked;
                config.warningRunMode = warningRunMode;
                localStorageSet('config', config);

                alert('设置成功！');
                reloadPage();
            });

            innerBoxDiv.querySelector('#configReset').addEventListener('click', function(){
//                localStorageSet(difficulty, difficultyDamageData = {});
                clearLocalStorage();
                alert('数据已清空');
                reloadPage();
            });


            var posX;
            var posY;
            titleDiv.addEventListener('mousedown', function(e)
            {
                posX = e.clientX - parseInt(boxDiv.offsetLeft);
                posY = e.clientY - parseInt(boxDiv.offsetTop);
                document.addEventListener('mousemove', mouseMove);
            });

            document.addEventListener('mouseup',function()
            {
                document.removeEventListener('mousemove', mouseMove);
                localStorageSet('configPanelLeft', boxDiv.style.left);
                localStorageSet('configPanelTop', boxDiv.style.top);
            });

            function mouseMove(ev)
            {
                if(ev==null) ev = window.event;//IE
                boxDiv.style.left = (ev.clientX - posX) + "px";
                boxDiv.style.top = (ev.clientY - posY) + "px";
            }

            UIHandler = boxDiv;
            document.body.appendChild(boxDiv);
        }

        this.show = function(){
            UIHandler.style.display  = 'block';
        }

        this.hide = function(){
            UIHandler.style.display  = 'none';
        }

        this.initialize();
    }

    function DamageInofUI(){
        var UIHandler;
        var timeText;
        var damageText;
        var damageProportionText;
        var damageTypeText;
        var monsterLevelText;

        this.initialize = function(){
            this.createUI();
        }

        this.createUI = function(){
            var boxDiv = document.createElement('div');
            boxDiv.style.boxShadow = '-1px 1px 3px 3px #666';
            boxDiv.style.height = 'auto';
            boxDiv.style.width = 'auto';
            boxDiv.style.position = 'absolute';
            boxDiv.style.zIndex = 1000;
            boxDiv.style.display = 'none';
            boxDiv.style.textAlign = 'left';

            var maskDiv = document.createElement('div');
            maskDiv.style.background = 'white';
            maskDiv.style.width = '182px';
            maskDiv.style.height = '255px';
            boxDiv.appendChild(maskDiv);

            var titleDiv = document.createElement('div');
            titleDiv.style.marginTop = '-250px';
            titleDiv.style.height = '17px';
            titleDiv.style.lineHeight = '18px';
            titleDiv.style.textAlign = 'center';
            titleDiv.style.fontWeight = 'bold';
            titleDiv.style.fontsize = '15px';
            titleDiv.innerHTML = applicationName + ' - 伤害信息';
            boxDiv.appendChild(titleDiv);

            var innerBoxDiv = document.createElement('div');
            innerBoxDiv.style.margin = '5px';
            innerBoxDiv.style.background = 'RGB(240,240,240)';
            innerBoxDiv.style.border = '1px solid RGB(152,152,152)';
            innerBoxDiv.style.padding = '10px';
            innerBoxDiv.style.width = '150px';
            innerBoxDiv.style.height = '200px';
            boxDiv.appendChild(innerBoxDiv);


            var infoTable = document.createElement('table');
            infoTable.style.fontSize = '13px';

            var timeTrUI = this.createInfoTr('时间', '5');
            timeText = timeTrUI.valueTd;
            infoTable.appendChild(timeTrUI.tr);

            var damageTrUI = this.createInfoTr('伤害值', '4');
            damageText = damageTrUI.valueTd;
            infoTable.appendChild(damageTrUI.tr);

            var damageProportionTrUI = this.createInfoTr('伤害比例', '3');
            damageProportionText = damageProportionTrUI.valueTd;
            infoTable.appendChild(damageProportionTrUI.tr);

            var damageTypeTrUI = this.createInfoTr('伤害类型', '1');
            damageTypeText = damageTypeTrUI.valueTd;
            infoTable.appendChild(damageTypeTrUI.tr);

            var monsterLevelTrUI = this.createInfoTr('怪物等级', '2');
            monsterLevelText = monsterLevelTrUI.valueTd;
            infoTable.appendChild(monsterLevelTrUI.tr);

            innerBoxDiv.appendChild(infoTable);
            UIHandler = boxDiv;
            document.body.appendChild(boxDiv);
        }

        /*{
         难度1:{
         怪物1：{
         等级：100,
         伤害值：9000,
         伤害比值：0.9，
         伤害类型：'crushing',
         次数：3
         }
         }
         }*/
//        时间：一小时前
//        伤害值：8000
//        比值：80%
//        伤害类型：火焰
//        怪物等级：20


        this.show = function(x, y, damageInfo){

            if(x)UIHandler.style.left = x + 'px';
            if(y)UIHandler.style.top = y + 'px';

            if(damageInfo){
                damageText.innerHTML = '<span style="color: red; font-weight: bold;">'+damageInfo.damage+'</span>';
                damageProportionText.innerHTML = '<span style="color: #ff5be8; font-weight: bold;">'+damageInfo.damageProportion+'</span>';
                damageTypeText.innerHTML = damageInfo.damageType;
                timeText.innerHTML = '<span style="color: #16898d;">'+this.formatTime(damageInfo.time)+'</span>';
                monsterLevelText.innerHTML = damageInfo.monsterLevel;
            }

            UIHandler.style.display = 'block';
        }

        this.hide = function(){
            UIHandler.style.display = 'none';
        }

        this.createInfoTr = function(label, value){
            var tr = document.createElement('tr');
            tr.style.lineHeight = '20px';

            var labelTd = document.createElement('td');
            labelTd.style.paddingRight = '20px';
            labelTd.innerHTML = label + ':';

            var valueTd = document.createElement('td');
            valueTd.innerHTML = value;

            tr.appendChild(labelTd);
            tr.appendChild(valueTd);
            return {
                tr: tr,
                valueTd: valueTd
            }
        }

        this.initialize();

        this.formatTime = function(time){
            var elapsedTime = parseInt((new Date().getTime() - time) / 60000);  //分钟

            if(elapsedTime > 7 * 24 * 60){
                return '1周前';
            }

            if(elapsedTime > 24 * 60){
                return parseInt(elapsedTime / (24 * 60)) + '天前';
            }

            if(elapsedTime > 60){
                return parseInt(elapsedTime / 60) + '小时前';
            }

            return elapsedTime + '分钟前';
        }
    }

    /**
     * 战斗场次：80/500
     * 上次死亡场次：60
     * 死亡时怪物数：8
     * @constructor
     */
    function BattleInfoUI(options){
        var UIHandler;
        var roundText;
        var deadRoundText;
        var deadMonstersText;

        this.createUI = function(){
            var battleUI = document.createElement('div');
            battleUI.style.boxShadow = '-1px 1px 3px 3px #666';
            battleUI.style.height = 'auto';
            battleUI.style.width = 'auto';
            battleUI.style.position = 'absolute';
            battleUI.style.zIndex = 1000;
            battleUI.style.display = 'none';
            battleUI.style.textAlign = 'left';
            battleUI.style.position = 'absolute';
            battleUI.style.top = localStorageGet('battleInfoPanelTop') || '300px';
            battleUI.style.left = localStorageGet('battleInfoPanelLeft') || '1245px';


            var maskDiv = document.createElement('div');
            maskDiv.style.background = 'white';
            maskDiv.style.width = '192px';
            maskDiv.style.height = '135px';
            battleUI.appendChild(maskDiv);

            var titleDiv = document.createElement('div');
            titleDiv.style.marginTop = '-130px';
            titleDiv.style.height = '17px';
            titleDiv.style.lineHeight = '18px';
            titleDiv.style.textAlign = 'center';
            titleDiv.style.fontWeight = 'bold';
            titleDiv.style.fontsize = '15px';
            titleDiv.style.cursor = 'move'
            titleDiv.innerHTML = applicationName + ' - 战斗信息';
            battleUI.appendChild(titleDiv);

            var innerBoxDiv = document.createElement('div');
            innerBoxDiv.style.margin = '5px';
            innerBoxDiv.style.background = 'RGB(240,240,240)';
            innerBoxDiv.style.border = '1px solid RGB(152,152,152)';
            innerBoxDiv.style.padding = '10px';
            innerBoxDiv.style.width = '160px';
            innerBoxDiv.style.height = '80px';
            battleUI.appendChild(innerBoxDiv);

            var infoTable = document.createElement('table');
            infoTable.style.fontSize = '13px';

            var roundTr = this.createInfoTr('当前场次', options.round + ' / ' + options.totalRounds);
            infoTable.appendChild(roundTr.tr);

            var deadRoundTr = this.createInfoTr('上次死亡场次', options.deadRound + ' / ' + options.deadTotalRounds);
            infoTable.appendChild(deadRoundTr.tr);

            var deadMonstersTr = this.createInfoTr('死亡时怪物数', options.deadMonsters);
            infoTable.appendChild(deadMonstersTr.tr);

            innerBoxDiv.appendChild(infoTable);
            UIHandler = battleUI;

            var posX;
            var posY;
            titleDiv.addEventListener('mousedown', function(e)
            {
                posX = e.clientX - parseInt(battleUI.offsetLeft);
                posY = e.clientY - parseInt(battleUI.offsetTop);
                document.addEventListener('mousemove', mouseMove);
            });

            document.addEventListener('mouseup',function()
            {
                document.removeEventListener('mousemove', mouseMove);
                localStorageSet('battleInfoPanelLeft', battleUI.style.left);
                localStorageSet('battleInfoPanelTop', battleUI.style.top);
            });

            function mouseMove(ev)
            {
                if(ev==null) ev = window.event;//IE
                battleUI.style.left = (ev.clientX - posX) + "px";
                battleUI.style.top = (ev.clientY - posY) + "px";
            }

            document.body.appendChild(battleUI);
        }

        this.show = function(){
            UIHandler.style.display  = 'block';
        }

        this.hide = function(){
            UIHandler.style.display  = 'none';
        }

        this.createInfoTr = function(label, value){
            var tr = document.createElement('tr');
            tr.style.lineHeight = '20px';

            var labelTd = document.createElement('td');
            labelTd.style.paddingRight = '0px';
            labelTd.innerHTML = label + ':';

            var valueTd = document.createElement('td');
            valueTd.innerHTML = value;

            tr.appendChild(labelTd);
            tr.appendChild(valueTd);
            return {
                tr: tr,
                valueTd: valueTd
            }
        }

        this.initialize = function(){
            this.createUI();
        }

        this.initialize();
    }


    function getAbsoluteTop(e){
        var offset = e.offsetTop;
        if(e.offsetParent != null) offset += getAbsoluteTop(e.offsetParent);
        return offset;
    }

    function getAbsoluteLeft(e){
        var offset = e.offsetLeft;
        if(e.offsetParent != null) offset += getAbsoluteLeft(e.offsetParent);
        return offset;
    }

    function addGlobalStyle(css) {
        var head, style;
        head = document.getElementsByTagName('head')[0];
        if (!head) { return; }
        style = document.createElement('style');
        style.type = 'text/css';
        style.innerHTML = css;
        head.appendChild(style);
    }
})();
