// ==UserScript==
// @name           Website Styler
// @namespace      websitestyler
// @description    Styles any page
// @include        *
// @version        1.8
// ==/UserScript==
var thecss, hideNoticeError, link_size, link_size_hover, link_padding, link_padding, select_border_color,
button_textfield_padding, button_textfield_padding_hover, link_border_color_hover,
button_textfield_border_color, button_textfield_border_color_hover, round_fields_intensity,
link_color, link_color_hover, textarea_border_color, textarea_border_color_hover,
modify_links, modify_buttons, modify_textareas, modify_selects;



//////////////////////////////////////////////////////////////////////////
//////////////////////////////////////////////////////////////////////////
// OPTIONS ///////////////////////////////////////////////////////////////
hideNoticeError = true; // Hide the caution box when installing scripts
//                      // Userscripts.org only

modify_links = false;
modify_buttons = true;
modify_textareas = true;
modify_selects = true;

link_size = "11";
link_size_hover = "11";

link_padding = ".1";
link_padding_hover = ".1";
button_textfield_padding = ".2";
button_textfield_padding_hover = ".2";

// Colors in hex or word (eg., #FF0000 or white)
// http://www.colorschemer.com/online.html
link_color = "#0078F0";
link_color_hover = "grey";
select_border_color = "grey";
link_border_color_hover = "black";
button_textfield_border_color = "#000";
button_textfield_border_color_hover = "#000";
textarea_border_color = "grey";
textarea_border_color_hover = "black";

// This option is for how rounded the fields are
round_fields_intensity = "4";
//////////////////////////////////////////////////////////////////////////
//////////////////////////////////////////////////////////////////////////
//////////////////////////////////////////////////////////////////////////





function addCss() {
thecss = "";
if(modify_buttons) {thecss += 
              "input,button {"
+                           "color: black !important;"
+                           "border: 1px solid "+button_textfield_border_color+" !important;"
+                           "font-family: arial, verdana, \"sans-serif\" !important;"
+                           "padding: "+button_textfield_padding+"em !important;"
+                           "-moz-border-radius-topright:"+round_fields_intensity+"px !important;"
+                           "-moz-border-radius-topleft:"+round_fields_intensity+"px !important;"
+                           "-moz-border-radius-bottomright:"+round_fields_intensity+"px !important;"
+                           "-moz-border-radius-bottomleft:"+round_fields_intensity+"px !important;"
+                           "background: url(data:image/png;base64,) !important;"
+"}"
+               "input:hover,button:hover {"
+                           "border: 1px solid "+button_textfield_border_color_hover+" !important;"
+                           "padding: "+button_textfield_padding_hover+"em !important;"
+                           "background:url(data:image/png;base64,) !important;"
+"}";
}
if(modify_textareas) {thecss += 
                "textarea {"
+                           "color: black !important;"
+                           "border: 1px solid "+textarea_border_color+" !important;"
+                           "font-family: arial, verdana, \"sans-serif\" !important;"
+                           "-moz-border-radius-topright:"+round_fields_intensity+"px !important;"
+                           "-moz-border-radius-topleft:"+round_fields_intensity+"px !important;"
+                           "-moz-border-radius-bottomright:"+round_fields_intensity+"px !important;"
+                           "-moz-border-radius-bottomleft:"+round_fields_intensity+"px !important;"
+                           "background: url(data:image/png;base64,) !important;"
+"}"
+               "textarea:hover {"
+                           "border: 1px solid "+textarea_border_color_hover+" !important;"
+"}";}
if(modify_selects) {thecss += 
                "select {"
+                           "color: black !important;"
+                           "background: url(data:image/png;base64,) !important;"
+                           "border: 1px solid "+select_border_color+" !important;"
+                           "font-family: arial, verdana, \"sans-serif\" !important;"
+"}";}
if(modify_links) {thecss += 
                "a {"
+                           "color: "+link_color+" !important;"
+                           "font-size: "+link_size+"px !important;"
+                           "font-family: arial, verdana, \"sans-serif\" !important;"
+                           "border: 1px solid transparent !important;"
+                           "padding: "+link_padding+"em !important;"
+"}"
+               "a:hover {"
+                           "color: "+link_color_hover+" !important;"
+                           "font-size: "+link_size_hover+"px !important;"
+                           "border: 1px solid "+link_border_color_hover+" !important;"
+                           "padding: "+link_padding_hover+"em !important;"
+                           "-moz-border-radius-topright:"+round_fields_intensity+"px !important;"
+                           "-moz-border-radius-topleft:"+round_fields_intensity+"px !important;"
+                           "-moz-border-radius-bottomright:"+round_fields_intensity+"px !important;"
+                           "-moz-border-radius-bottomleft:"+round_fields_intensity+"px !important;"
+"}";}

GM_addStyle(thecss);
}

function uso() {
document.images[0].src = "data:image/png;base64,";
GM_addStyle("#header {background:url(data:image/png;base64,);}");

if(hideNoticeError === false) GM_addStyle(".notice,.error {background: transparent !important; color: black !important; border: 1px solid #B5B5B5  !important;}");
else {
if(location.href.indexOf("http://userscripts.org/scripts/show/") !== -1)
document.getElementById("install_warning").style.display = "none";
}
GM_addStyle("#mainmenu a {color:#08f !important}");
}

addCss();
if(location.href.indexOf("http://userscripts.org") !== -1) window.addEventListener("load",uso,false);