// ==UserScript==
// @name           Govn0 smileys
// @version	  	   3.2.0
// @description    Adds smileys to imageboards
// @homepage       http://bit.ly/g0vn0
// @encoding       utf-8
// @include        *
// @exclude        */src/*
// @exclude        */kusaba.php
// @exclude        */menu.php
// @exclude        */news.php
// @exclude        http://iichan.ru/n/*
