	_addTab : function (msg, head){
		var t = Tabs.Tower;
		if (!head &&Data.options.tower.nospy && msg.type == 1)
			return;
		var rows = t.logTab.getElementsByTagName('tr');
		var rowCount = rows.length;
		var row = t.logTab.insertRow((head==true? 0 : 1));
		row.vAlign = 'top';
		if (head) {
			row.id = 0;
			row.className = UID['row_headers'];
		} else {
			if (rowCount % 2 == 1) row.style.backgroundColor = "rgba(255, 255, 255, 0.4)";
			else row.style.backgroundColor = "";
			row.id = msg.id;
		}
		var cell;
		var txt;
		for (var i=0;i<5;i++) {
			cell = row.insertCell(i);
			if (head) {
				switch (i) {
					case 0: txt = translate('Type'); break;
					case 1: txt = translate('Arrival time'); break;
					case 2: txt = translate('Attacker'); break;
					case 3: txt = translate('Player / Alliance'); break;
					default: txt = translate('Troops'); break;
				}
			} else {
				switch (i) {
					case 0: txt = (msg.type == 0 ? '<span class="' + UID['doa-icons'] + ' i-Attacks" ></span>' : '<span class="' + UID['doa-icons'] + ' i-Spying" ></span>'); break;
					case 1: if (msg.arrive_at != 0) txt = new Date(msg.arrive_at).formatTime();
							else txt = '';
							break;
					case 2: if (msg.x != -1) txt = msg.x+','+msg.y;
							else txt = '';
							break;
					case 3: txt = ' ' + msg.alliance; break;
					default: txt = msg.troups; break;
				}
			}
			cell.innerHTML = txt;
			if (i==0) {
				cell.width = '5%';
				cell.style.textAlign = 'center';
			}else if (i==1) {
				cell.width = '15%';
				cell.style.textAlign = 'center';
			}else if (i==2){ 
				cell.width = '10%';
				cell.style.textAlign = 'center';
			}else if (i==3){ 
				cell.width = '30%';
			}else if (i==4){ 
				cell.width = '35%';
			}
			if (head) {
				cell.style.fontWeight = 'bold';
			} else {
				cell.style.fontWeight = 'normal';
				cell.style.color = 'Black';
			}
		}
	}, 
