var WinLog = {
	state : null,
	win: null,
	eOut : null,
	lastE : null,
	enabled : true,
	reverse : true,
	busy : false,
	isOpening : false,
	lineNum : 0,

	open : function (){
		var t = WinLog;

		function eventButClear(){
			var t = WinLog;
			t.lastE = null;
			t.eOut.innerHTML ='';
			t.lineNum = 0;
		}
		function eventButReverse(){
			var t = WinLog;
			if (t.busy) return;
			t.busy = true;
			if (t.reverse){
				t.win.document.getElementById('wlRev').value= 'Top';
				t.reverse = false;
			} else{
				t.win.document.getElementById('wlRev').value= 'Bottom';
				t.reverse = true;
			}
			var n = t.eOut.childNodes.length;
			if (n < 2) return;
			for (i=n-2; i>=0; i--){
				t.eOut.appendChild (t.eOut.childNodes[i]);
			}
			t.busy = false;
		}

		if (!t.win || t.win.closed){
			t.isOpening = true;  
			// Firefox bug??? It appears as if a new thread is started on open, withOUT reusing same window
			t.win = window.open('', 'uwtrace', 'top=30,left=0,width=900,height=700,scrollbars=no,location=no,menubar=no,directories=no,status=no');
			t.isOpening = false; 
			t.state = null; 
		}

		if (t.state == null){
			t.win.document.body.innerHTML = '\
				<STYLE>pre{margin:0px} hr{margin:3px; height:1px; border:0px; color:#cee; background-color:#cee}</style>\
				<BODY style="margin:0px; padding:0px; border:none">\
				<DIV id=winlogtop style="background-color:#d0d0d0; margin:0px; padding:0px; border:1px solid">\
				<INPUT id=wlClear type=button value="'+translate('Delete')+'" /> &nbsp; <INPUT id=wlRev type=button value="Bottom" ></div>\
				<DIV id=wlOut style="overflow-y:auto; height:100%; max-height:100%"></div></body>';
			t.win.document.getElementById('wlClear').addEventListener('click', eventButClear, false);
			t.win.document.getElementById('wlRev').addEventListener('click', eventButReverse, false);
			t.eOut =  t.win.document.getElementById('wlOut');
			t.lastE = null;
			t.state = 1;
		}
	},

	writeText : function (msg){
		WinLog.write (msg.escapeHTML()); 
	},
	write : function (msg){
		var t = WinLog;
		if (!t.enabled || t.isOpening)
		return;
		t.open();
		var te = document.createElement('pre');
		if (++t.lineNum % 2)    
			te.style.backgroundColor = '#eeeeee'; 
		else te.style.backgroundColor = '#ffffff';  
		var now = new Date();
		var m = [];
		var millis = now.getMilliseconds();
		m.push (now.toTimeString().substring (0,8));
		m.push ('.');
		if (millis < 100) m.push('0');
		if (millis < 10) m.push('0');
		m.push(millis);
		m.push (': ');
		m.push (msg);
		te.innerHTML = m.join('');

		if (t.reverse){
			if (t.lastE == null){
				t.eOut.appendChild(te);
				t.lastE = te;
			} else {
				t.eOut.insertBefore(te, t.lastE);
			}
			var hr = document.createElement('hr');
			t.eOut.insertBefore(hr, te);
			t.lastE = hr;
		} else {
			t.eOut.appendChild(te);
			t.eOut.appendChild(document.createElement('hr'));
		}
	}
};

