function saveValue(obj, value)
{
  if (IsChrome) localStorage.setItem(obj, value);
  else GM_setValue (obj, value);
}

function loadValue(obj, defValue)
{
	var s;
	if (IsChrome) s = localStorage.getItem(obj);
	else s = GM_getValue (obj);
	if (!s && defValue) s = defValue;
	return s;
}

function addStyle(css) {
	if (IsChrome) {
		var head = document.getElementsByTagName('head')[0];
		if (!head) {return} 
		style = document.createElement('style'); 
		style.type = 'text/css'; 
		try {
			style.innerHTML = css;
		}
		catch(e) {
			style.innerText = css;
		} 
		head.appendChild(style); 
	}
	else GM_addStyle (css);
}
