// Append global style
var globalstyle = document.createElement('link');
globalstyle.rel = 'stylesheet';
globalstyle.type = 'text/css';
globalstyle.href = 'https://dl.dropboxusercontent.com/u/161626803/Classic%20YouTube!%203.0.css';
document.getElementsByTagName('head')[0].appendChild(globalstyle);

//Show tags
function main() {
    var metas = document.getElementsByTagName('meta'); 
    var tags = ""; 
    for (var i = 0; i < metas.length; i++) { 
        if (metas[i].getAttribute("name") == "keywords") { 
            tags = metas[i].getAttribute("content"); 
        } 
    }   
    var extras_section = document.getElementsByClassName("watch-extras-section")[0];
    extras_section.innerHTML += "<li id=\"tags\"><h4 class=\"title\">Tags</h4><div class=\"content\" id=\"tags_content\">" + tags + "</div></li>";
}
document.addEventListener('DOMContentLoaded', main, false);

