	function GB_Validate()
	{
		if(GB.Validate[0] == 'attack'){
			params = GB.Validate[1];
			// Resource looting 	type:"ask_farm_for_resources"
			// Increase strength 	type:"farm_attack"
			// 	Land attack				attacking_strategy:"regular"
			var Action = "";
			if(params['type'] == 'ask_farm_for_resources')		{Action = L("FARMASK");}
			else if(params['type'] == 'farm_attack')					{Action = L("FARMSTR");}
			else if(params['attacking_strategy'] == 'regular') 	{Action = L("LANDATK");}
			movement = {'id' : Math.ceil(1000000000*Math.random()) ,'arrival_at' : (params['time']+Math.floor((new Date().getTime())/1000)) ,'command_name' : Action , 'incoming' : false , 'town': {'id':params['id'] , 'name' :params['name'] } };
			GB.MovementsN.push(movement);
			GM_setValue("MovementsN"+GB.Game.townId,GB.MovementsN.toSource());
			GB_Movements();
		}
		GB.Validate = [];
	}
